css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.okcupid.com") {
div#matches_block .item .image, #matches_block .item img {
    visibility: hidden;
}
#recent_activity .user_info, #recent_activity .image img, #recent_activity .image {
    display: none;
}
div.section.recent ul li a img {
    display: none;
}
div.section.matches li.match img {
    display: none !important;
}
.photobrowser .thumbnails li img {
    display: none;
}

div.tabbed_heading div#profile_thumbs div.thumb img {
    display: none !important;
}
#main_column div.photo div.img img {
    display: none !important;
}
div#right_column ul#similar_users_list li a.user_image {
    display: none;
}
ul#messages li a.photo img, ul#messages li a.okcupid img {
    display: none !important;
}
ul#thread li .photo img {
    display: none !important;
}
div.user_row_item .user_image img {
    display: none;
}
.sidebar p, .sidebar li#nav_ratings {
    display: none;
}
div#event_display_body div.orbit_event a.thumb img {
    display: none;
}
a.user_image {
    visibility: hidden;
}
ul#matches.new li a.photo {
    display: none;
}
div.user_row p.user_thumb {
    display: none;
}
div#action_bar div.image img {
    display: none !important;
}
div#right_column div#profile_details dd#ajax_ethnicities {
    line-height: 0em;
    display: none;
}
}
