css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blondie.com") {

    html, body, #comic-wrap, #access {
        background:#330101!important;
    }
    
    #comic-wrap {
        padding-top:30px!important;
    }
    
    #access {
        padding-top:50px!important;
    }
    
    #access a {
        color:#DDD!important;
    }
    
    #access {
        color:#F44!important;
    }
    
    #comic {
        background:#DDD!important;
    }
    
    #mainwrap, #comic-wrap, #access  {
        border:0px!important;
    }

    #above-header, #branding, footer, #primary, #secondary, #buycomic, .addtoany, .addtoany_comic, #kfcwr_tb {
        display:none!important;
        visibility:hidden!important;
    }

}
