css:
@-moz-document url-prefix("http://joyreactor.cc/") {

@font-face {
	font-family: 'uni';
	src: url('https://dl.dropboxusercontent.com/u/69750527/uni0553-webfont.eot?') format('eot'), 
		url('https://dl.dropboxusercontent.com/u/69750527/uni0553-webfont.woff') format('woff'), 
		url('https://dl.dropboxusercontent.com/u/69750527/uni0553-webfont.ttf')  format('truetype');
	}

* {
	font-family: 'uni' !important;
	text-transform: uppercase !important;
	}

#header {
    height: 44px !important;
    }

#header .logo {
    padding: 0px 0px 0px !important;
    }
	
#header .logo a, #header .logo a:hover {
    width: 200px !important;
    height: 44px !important;
    background: url("https://dl.dropboxusercontent.com/u/69750527/839577.png") no-repeat scroll 0px 0px transparent !important;
    }

#header .description {
    display: none !important;
    }

#navcontainer {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0px 0px #E45C10 !important;
    border: 2px solid black;
    width: 1107px;  
    border-radius: 3px 3px 0px 0px !important;  
    }

#navcontainer ul li {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") #E45C10 !important;
    height: 23px !important;
    }

#navlist li span a {
	text-transform: uppercase !important;
    }
    
#navcontainer ul li {
    border-right: 2px solid black;
    }

#first, .lastitem {
    border-right: none !important;
    }

#searchBar {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0px 100% white !important;
    border: 2px solid black !important;
    border-top: none !important;
    color: black !important;
    border-radius: 0px 0px 3px 3px !important;
    }
  

#searchBar a {
    color: black !important;
    }
    
#submenu {
    width: 840px !important;
    }
    
#searchmenu {
    height: 15px !important;
    }

#searchform {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0 0 transparent !important;
    border: 2px solid black !important;
    color: black !important;
    height: 15px !important;
    }
    
#searchsubmit {
    background: none repeat scroll 0 0 transparent;
    border: 0 none;
    cursor: pointer;
    height: 15px;
    line-height: 15px;
    text-indent: -9999px;
    vertical-align: top;
    width: 20px;
    }
 
#container {
    background: url("https://dl.dropboxusercontent.com/u/69750527/bg.png") repeat-x scroll center top transparent !important;
    width: 100% !important;
    min-width: 100% !important;
    }
    
#contentinner {
    background: none repeat scroll 0 0 #FFFFFF;
    border: 2px solid black !important;
    border-radius: 0px !important;
    padding: 10px 9px;
    border-radius: 3px 3px 3px 3px !important;
    }

#add_post div.add_post_tags {
    height: 28px !important;
    }

.article .ufoot, .post_description {
    color: black !important;
    }
    
.article .uhead a {
    color: #FAB728 !important;
    text-decoration: none !important;
    }
    
.comment .avatar {
    background-image: url("https://dl.dropboxusercontent.com/u/69750527/ico_comment.png") !important;
    width: 16px !important;
    }

div.vote-plus {
    background: url("https://dl.dropboxusercontent.com/u/69750527/ico_plus.png") transparent !important;
    height: 12px !important;
    width: 12px !important;
    }

div.vote-minus {
    background: url("https://dl.dropboxusercontent.com/u/69750527/ico_minus.png") transparent !important;
    height: 12px !important;
    width: 12px !important;
    }
    
#sidebar ul li div.sideheader, #sidebar .sideheader {
    color: #FAF68C;
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0px 0px #E45C10 !important;
    border: 2px solid black;
    border-radius: 3px 3px 0px 0px !important;
    }

.sidebarContent {
    border: 2px solid black !important;
    border-radius: 3px !important;
    }
    
#add_post input[type="submit"] {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0 0 #FAB728 !important;
    color: white !important;
    margin: 2px 0 0 7px !important;
    padding: 2px 0 4px !important;
    text-align: center;
    text-shadow: none !important;
    vertical-align: top;
    width: 78px;
    }

#add_post > div > div.add_post_footer {
    height: 24px !important;
    margin: 2px 0 10px !important;
    }
    
.favorite_blog_link p.add_to_fav {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0 0 #FAB728 !important;
    border-radius: 3px !important;
    color: white !important;
    height: 29px;
    line-height: 12px;
    text-align: center;
    text-decoration: none;
    vertical-align: top;
    width: 120px;
    }

.favorite_blog_link .add_to_fav a {
    text-shadow: none !important;
    }

.article {
    border: none !important;
    border-bottom: 2px solid black !important;
    }

#submenu div.submenuitem.active a, #submenu b, #submenu div.submenuitem.active a:hover, #submenu a:hover, #submenu span.active a:hover {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 100% 100% transparent !important;
    color: #FAB728 !important;
    }

#add_post span.super-tag {
    background-color: white;
    border: 2px solid #e45c10 !important;
    border-radius: 3px !important;
    color: #e45c10 !important;
    cursor: pointer;
    padding: 1px 6px;
    }
    
input#tag, .post_share_view input, .sfSignin input[type="text"], .sfSignin input[type="password"], .sf_apply input[type="text"], .sf_apply input[type="password"], #demotivator_form input[type="text"], .addcomment input[type="text"], #add_post input[type="text"], #add_poll input[type="text"] {
    border: 2px solid black !important;
    }

#add_post textarea {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0 0 transparent !important;
    border: 2px solid black !important;
    }

#submenu div.submenuitem, #submenu span {
    background: url("https://dl.dropboxusercontent.com/u/69750527/blank.png") no-repeat scroll 0 0 transparent !important;
    border-right: 2px solid black !important;
    }

#sidebar ul li:first-child div.sideheader, #sidebar > div:first-child .sideheader {
    color: #FAF68C !important;
    }

#sidebar ul li:first-child div.sideheader, #sidebar > div:first-child .sideheader a {
    color: #FAF68C !important;
    }
}
