css:
@-moz-document domain("fanfiction.net") {
div#content_wrapper, div#p_footer, div#zmenu, div.menulinks {
max-width:none!important;
min-width:0px!important;
}

div#content_wrapper {
background-color: black!important;
}

.xcontrast {
background-color: black;
}

#content_wrapper_inner {
border-left: 0px solid #d4d4d4;
border-right: 0px solid #d4d4d4;
border-bottom: 0px solid #d4d4d4;
outline: 0;
}

div a.a2a_dd {
display:none!important;
}
img.lazy.cimage {
display:none !important;
}
img.cimage {
display:none !important;
}
div#google_ads_div_ff_safe_banner_728x90_ad_wrapper, div#google_ads_div_ff_banner_728x90_ad_container, div#p_footer, div#google_ads_div_ff_rectangle_300x250_ad_wrapper {
display:none!important;
}
div#google_ads_div_ff_tower_160x600_ad_wrapper, div#google_ads_div_ff_text_160x600_ad_wrapper {
display:none!important;
}

body {
height: 100%;
min-width: 0px!important;
background:#000000!important;
font-family:Verdana,sans-serif !important;
font-size:small !important;
color: white;
}

.maxwidth {
min-width: 0px;
width: 800px!important;
}

a:link {
    color: #0033CC;
    text-decoration: none;
}

a:hover {
    border-bottom: 1px solid #9999FF;
    color: #CC0044;
}
a:active {
    color: #0033CC;
    text-decoration: none;
}
a:visited {
    color: #7A378B !important;
}
.reviews {
    color: #FF5500 !important;
    font-style: italic;
    text-decoration: none !important;
}
a.reviews {
    color: #FF5500 !important;
    font-style: italic !important;
    text-decoration: none !important;
}
a.visited.reviews {
    color: #FF5500 !important;
    font-style: italic !important;
    text-decoration: none !important;
}
.menulinks {
    color: white !important;
    letter-spacing: 0.5px;
}
.menulinks a:visited, .menulinks a:link, .menulinks a:active, .menulinks a:hover {
    color: #FFFFFF !important;
    text-decoration: none;
}
.zmenu a:visited, .zmenu a:link, .zmenu a:active, .zmenu a:hover {
    color: #000000 !important;
    text-decoration: none;
}
div#name_login a:visited, .name_login a:link, .name_login a:active, .name_login a:hover {
    color: #ff5500 !important;
    text-decoration: none;
    }
}
