css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain(facebook.com) {
* {
  background-color: #000 !important
}
html, body {
  background: none !important;
  color: gainsboro!important;
  background-color: #333333 !important
}
pre {
  background-color: #1a1a1a !important;
  border: 1px solid darkgray !important
}


a:link { 
  olor: #FF7D01 !important
}
a:visited {
  color: #FCCDA2 !important
}
label {
  color: gainsboro!important
}
input {
  color: gainsboro!important;
  background-color: #000 !important
}
select {
  color: gainsboro!important;
  background-color: #000 !important
}
textarea {
  color: gainsboro !important;
  background-color: #000 !important
}


.clearfix {
  background: none !important;
  background-color: #000 !important
}
.body {
  background: none !important;
  color: gainsboro!important
}
#navigator {
  background: #000 !important
}
#app_list .list_item {
  background: none !important
}
#buddy_count {
  color: #FFF !important
}
.buddy_count_num strong {
  color: #FFF !important;
}
.textarea {
  color: #FFF !important;
}
.link_btn_style {
  background-color: #333 !important
}
.wrapper {
  color: #CCC !important
}
.wrapper em {
  color: #FFF !important
}


#gift_message_body_wrapper {
  background-image: url(data:image/png;base64,%3D%3D) !important
}

#gift_message_body {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,%3D%3D) !important }

.gift_topright {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,%3D) !important }

.gift_topleft {
  width: 0 !important;
  height: 6px !important;
  padding-left: 6px !important;
  background-image: url(data:image/gif;base64,%3D ) !important }

.gift_bottomright {
  width: 0 !important;
