css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.penny-arcade.com") {
    /* Kill all the colors */
    body {
        background-color: #f2daf2 !important;
    }
    #header, .pixels {
        background: #f2daf2 !important;
        height: 110px !important;
    }
    #header h1,
    #header ul {
        display: none !important;
    }
        
    .ProfileOptions, .BeforeCommentHeading, .PageControls.Top {
        top: 165px !important;
    }
    #Frame, #FootCrumbs {
        background: #f2daf2 !important;
    }
    #Foot, #footer {
        display: none;
    }
    a,
    .Number,
    .SpoilerTitle input,
    #Panel .About dl dt
    {
        color: #000000 !important;
    }
    .Message a {
        color: #6a2c69 !important;
    }
    .PageTitle h1,
    .CommentForm h2,
    .HomepageTitle,
    .Box h4,
    .H
    {
        background: none !important;
        border: none !important;
        box-shadow: none !important;
        color: #000000 !important;
        text-shadow: none !important;
    }
    .DataTable thead td {
        background: none !important;
        color: #000000 !important;
        text-shadow: none !important;
        border: none !important;
    }
    .CategoryName, .DiscussionName {
        background: none !important;
    }
    textarea:focus, input:focus,
    .Inputbox input:focus{
        background: #fdd5fd !important;
    }
    .Button, .NavButton, .ProfileButtons,
    .PageControl a:not(.Previous),
    .HasNew {
        background: #ffbdf8 !important; 
        color: #000000 !important;
        text-shadow: none !important;
        box-shadow: none !important;
    }
    .PageControl a.Highlight {
        background: #ca2ec8 !important; 
    }
    .Previous, .Next, .Ellipsis, .Elipsis {  
        background-color: #fd6bfb !important;

    }
    .Bookmark { 

    }
    .UserSpoiler {
        background: none !important;
    }
    code, pre {
        background: #ffdbfd !important;
        border: 1px solid #aaaaaa !important;
    }
    .Popup .Body {
        background: #ffd7ff !important;
    }
    .OptionsTitle {
        background: none !important;
        width: 50px !important;
        font-size: 12px !important;
        line-height: inherit !important;
    }
    
        
