css:
@-moz-document url-prefix("http://beta.mtgsalvation.com/forums") {
body {
  background: white !important;
}

table.listing .b-table-heading {
  background: #587C6B !important;
  border: 1px solid #BFE3BF !important;
}

table.listing tr td {
  border: 1px solid #BFE3BF !important;
}

table.listing tr span {
  color: black !important;
}

table.listing tr td.col-status a,
table.listing tr i.u-icon-jump {
  background: #6E9884 !important;
}

table.listing tr td:nth-child(even) {
  background: #EDF8EF !important;
}

table.listing tr td:nth-child(odd) {
  background: #DBEFDE !important;
}

table.listing tr a, table.listing tr span.user {
  color: #226C22 !important;
  text-decoration: underline;
}

h4.title a {
  font-weight: bold !important;
}

div.forum-post-header {
  background: #587C6B !important;
  border: 1px solid #D1D1E1 !important;
}

div.p-comment-wrapper, div.p-comment-footer {
  background: #EDF8EF !important;
  border: 0 !important;
}

span.p-comment-username span.user {
  color: #185018 !important;
  font-size: 14pt;
  font-weight: normal;
}

div.p-comment-user-background {
  background: #DBEFDE !important;
  border: 1px solid #D1D1E1 !important;
}

div.forum-post-body {
  color: black !important;
}

div.forum-post-body a {
  color: #185018 !important;
}

blockquote.source-quote {
  background: #DBEFDE !important;
  border-style: inset;
  border-radius: 0;
}

div.quote-body {
  color: black;
  font-style: italic;
}

div.quote-source {
  font-style: normal;
}

div.quote-source a.quote-author {
  color: black;
  font-weight: bold;
}

span.b-pagination-item,
ul.user-actions-threads li.user-action>a,
ul.user-actions-forums li.user-action>a {
  border: 1px solid #D1D1E1 !important;
  background: #6E9884 !important;
}

li.b-pagination-item a:hover {
  border: 1px solid #D1D1E1 !important;
  background: #587C6B !important;
}

div.form-actions button.u-button-default,
div.form-actions button.u-button-primary {
  background: #6E9884 !important;
}

div.form-actions button.u-button-link {
  color: #185018 !important;
}
}
