@-moz-document domain("girlswithslingshots.com") {
    
    /* Just to get rid of most of the stuff around the comic. */
    #header, #undercomic, #pagefoot, #footer, #blogheader {
        display:none!important;
    }
    
    html, body, #comicarea, #comichead, #nav, #page {
        background: /*[[BACKGROUND_COLOR]]*/!important;
