css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix("http://droptun.es/app") {
    /*-------------START------------*/
    /*---------------position*/
    #nav{bottom:0!important;}
    .social{display:none !important;}
    .jp-audio{
        border-top:0 !important;
        background: #3f4c6b !important; /* Old browsers */
    }
    .jp-interface{width:80% !important;margin:0 auto !important;}
    li.dir.child,li.cur{padding-left:10px!important;width:140px;}
    
    .dirs{
        z-index:0;
        position:fixed;
        height:100%;
        overflow-y:auto;
    }
    .tunes{
        display:block !important;
        margin-left:150px!important;
    }
    
    /*------ background ------*/
    #lvl-1{
        z-index:1;
        background: #3f4c6b !important; /* Old browsers */
    }
    .header{
        background-color:#222;
        opacity:0 !important;
        transition:linear .3s opacity 
        }
    .header:hover{opacity:1 !important;}
    .browser .cur{background: #499bea !important; /* Old browsers */}
    
    /*--------font||color||interface*/

    a:hover{text-decoration:none!important;}
    *::-moz-selection{color:#fff;}
    .tune,.thedir,.jp-current-time,.jp-duration{
        font-family: 'Open Sans','PT Sans','Ubuntu','Hiragino Sans GB', sans-serif !important;
        font-size:14px !important;
    }
    .jp-playlist,.jp-current-time,.jp-duration{
        color:#f3faff !important;
        text-shadow:0 1px 0 #222 !important;
    }
    .col-2,.jp-current-time,.jp-duration{width:50px !important;}
    .jp-playlist{
        font-size:18px !important;
        font-family: 'Open Sans Condensed','PT Sans','Ubuntu','Hiragino Sans GB', sans-serif !important;
        height:26px !important;
        padding:0 0 8px !important;
    }
    
    /*---------------------icon*/
    .jp-play{background:url('http://i1287.photobucket.com/albums/a638/cirrp/Media-Play-48.png') !important;}
    .jp-pause{background:url('http://i1287.photobucket.com/albums/a638/cirrp/Media-Pause-48.png') !important;}
    .jp-previous{background:url('http://i1287.photobucket.com/albums/a638/cirrp/Media-Rewind-32.png') !important;}
    .jp-next{background:url('http://i1287.photobucket.com/albums/a638/cirrp/Media-Fast-Forward-32.png') !important;}
    .jp-next, .jp-previous{width:32px !important;height:32px !important;}
    
/*
* .tune-stop{background:url('http://i1287.photobucket.com/albums/a638/cirrp/Media-Play-24.png') -2px -2px #499bea !important;}
* .tune-playnow{background:url('http://i1287.photobucket.com/albums/a638/cirrp/Media-Play-24.png') -2px -2px #aaa !important;}
*/
    .tune-action span:hover{text-decoration:none!important;}
    /*.tune-playnow,.tune-stop{background-image:none !important;}*/
    .tune-playnow:after,.tune-stop:after{
        display:block;
        content:'\25B6';
        text-align:center;
        color:#fff;
        font-size:12px!important;
        text-shadow: 0 1px #777;
        border-radius:2px;
        line-height:20px !important;
        height:24px!important;
        width:24px;
    }
    .tune-playnow:after{background:#aaa 0 0 no-repeat;}
    .tune-stop:after{background:#499bea;}
    
    #nav>li:last-child{width:80px;margin-left:10px!important;}
    #nav>li:last-child:before{
        display:block;
        content:'\1F50A';
        vertical-align:bottom;
    }
    
    /*-------------------bars */
    .jp-volume-bar,.jp-volume-bar-value,
    .jp-progress,.jp-seek-bar,.jp-play-bar{
        border-radius:3px !important;
        height:6px !important;
    }
    .jp-seek-bar,.jp-volume-bar{background:#ecefff !important;}
    .jp-play-bar,.jp-volume-bar-value{background:#499bea !important;}
    
    .jp-volume-controls{bottom:14px !important;left:14px;}
    
    /*---------smaller screen------*/
    @media screen and (max-width:768px){
	.col-3{
		display:block !important;
		width:90%;
		padding:10px 0 !important;
	}
	.col-2{float:left}
	.col-4{float:right}
	.dirs{
	    padding:161px 0 20px !important;
		width:130px !important;
		background-color:#e9edf0 !important;
	}
	.dirs-list{width:130px !important;}
	.dirs input{
        width:75px !important;
	    background-position:100px 7px !important;
	}
	li.dir.child,li.cur{width:120px;}
    .tunes{
        margin-left:130px!important;
    }
	.tunes{padding-top:166px !important;}
	.header{height:60px !important;}
    .header h1{top:10px !important;}

    #nav>li:last-child{
        position:absolute !important;
        top:-20px;
        right:10px;
    }
    .jp-interface{width:100% !important;height:100px !important;}
}

    /*----------- FONTS -------------*/
    @font-face {
        font-family: 'Open Sans';
        font-style: normal;
        font-weight: 400;
        src: local('Open Sans'), local('OpenSans'), url(https://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
    }
    @font-face {
        font-family: 'Open Sans Condensed';
        font-style: normal;
        font-weight: 700;
        src: local('Open Sans Cond Light'), local('OpenSans-CondensedLight'), url(http://themes.googleusercontent.com/static/fonts/opensanscondensed/v6/gk5FxslNkTTHtojXrkp-xONSK5BxN3NFS4EJkViHIqo.woff) format('woff');
    }
    
    /*----- END -----*/

}
