css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ppsspp.org") {
.trow1, .trow_sep, #container, blockquote, textarea, code, .textbox, .alert, #content {
    background: #2C2C2C !important;
}
.trow2, #spinner, #mce_editor_0_editor_tab a, #mce_editor_0_code_tab a, .containerHead, .plainlinks, #mw-head, .sidebar-nav, .infobox, .wikitable, .toc, .infobox tbody tr {
    background: #333 !important;
}
.pm_alert, .tcat, .bottommenu, .highlight, .tborder, #panel, .spoiler, .spoiler_contenido, .spoiler_cabecera, .codeblock, .trow_sep, .pagination_current,  pre, .active a, .wikitable tbody tr th, .headerSort, .catlinks, .thumbinner, .collapsed.jquery-tablesorter tfoot tr th, .collapsed.jquery-tablesorter thead tr th {
    background: #111 !important;
    color: #eee !important;
}
.thead {
    background: #000 !important;
}
.trow1, trow2 {
    border-bottom: 1px solid #111 !important;
    border-top: 1px solid #111 !important;
}
a {
    color: steelblue !important;
}
a, b, li, p, h1, h2, h3, h4, span, div {
    text-shadow: none !important;
}
span, .smalltext, .trow_sep, textarea, body, .textbox, div, h1, h2, h5, li, th, .version, .reldate, td, p {
    color: #eee !important;
}
a.btn {
    color: #111 !important;
}
.post_body span[style="color: #0000CD;"] {
    color: #99CCFF !important;
}
.post_body span[style="color: #FFA500;"] {
    color: #FFA500 !important;
}
.post_body span[style="color: #32CD32;"] {
    color: #32CD32 !important;
}
.post_author span[style="color: #CC00CC;"] {
    color: #DD4455 !important;
}
}
