css:
@-moz-document domain('felisfire.com'), url-prefix('http://felisfire.com'){ body {

	margin: 0px;

	background-image: url('http://i.imgur.com/3LmvGMt.png');

	background-color: #161616;

}

body, td {

	font-family: Arial;

	color: #FFFFFF;

	font-size: 13px;

}

a, a:visited, a:active {

	color: #06e8d7;

	font-weight: bold;

	text-decoration: none;

}

a:hover {

	text-decoration: bold;

}

img {

	border: none;

}



div {

	text-align: center;

}

.left {

	text-align: left;

}

.right {

	text-align: right;

}

input, select, textarea {

	background-color: #161616;

	color: #06e8d7;

	font-weight: bold;

	font-size: 12px;

	font-family: Arial;

}

textarea.breathe {

	width: 300px;

	height: 100px;

}

textarea.breathebig {

	width: 500px;

	height: 400px;

}

input.breathe {

	width: 200px;

}

select {

	cursor: pointer;

}

select:hover {

	background-color: #161616;

}

table, div {

	margin-left: auto;

	margin-right: auto;

}



table.its {

	border-spacing: 5px;

}

table.its td {

	background-color: #161616;

}

table.valign td {

	vertical-align: top;

}

td, th {

	text-align: center;

	padding: 5px;

}

th {

	color: #efdecc;

	font-weight: bold;

	font-size: 12px;

	background-color: #161616;

}

td {

	background-color: #161616;

}





hr.div {

	height: 1px;

	background-color: #161616;

	border: none;

}

button, input[type=button] {

	cursor: pointer;

	color: #000000;

text-shadow: white 0px 0px 3px;


	font-weight: bold;

	width: 75px;

	height: 23px;

	border: none;

	background-color: transparent;

	background-image: url('http://i.imgur.com/hJRltsZ.png');


	font-family: Arial;

	font-size: 12px;

}

button:hover, input[type=button]:hover {

	background-image: url('http://i.imgur.com/vKOZ6Xk.png');

}

button:active, input[type=button]:active {

	color: #DDDDDD;

	background-image: url('http://i.imgur.com/hJRltsZ.png');

}

button.num {

	width: 50px;

	height: 23px;

	background-image: url('http://i.imgur.com/vKOZ6Xk.png');

}

button.num:hover {

	background-image: url('http://i.imgur.com/vKOZ6Xk.png');

}

button.num:active {

	background-image: url('http://i.imgur.com/hJRltsZ.png');

}

button.thin {

	width: 100px;

	height: 20px;

	background-image: url('http://i.imgur.com/dnd6e7I.png');

}

button.thin:hover {

	background-image: url('http://i.imgur.com/HCVYA5O.png');

}

button.thin:active {

	background-image: url('http://i.imgur.com/HCVYA5O.png');

}

button.wide, input[type=button].wide {

	width: 150px;

	height: 23px;

	background-image: url('http://i.imgur.com/dnd6e7I.png');

}

button.wide:hover, input[type=button].wide:hover {

	background-image: url('http://i.imgur.com/HCVYA5O.png');

}

button.wide:active, input[type=button].wide:active {

	background-image: url('http://i.imgur.com/HCVYA5O.png');

}



div.note {

	background-color: #161616;

	padding: 5px;

	color: #06e8d7;

	font-size: 12px;

	width: 80%;

}

div.topnote {

	background-color: #161616;

	padding: 5px;

	margin-bottom: 20px;

	color: #efdecc;

	font-size: 12px;

	width: 50%;

}

div.notif {

	background-color: #161616;

	padding: 5px;

	width: 60%;

	margin-bottom: 1px;

	color: #d6fffc;

	font-size: 12px;

	font-weight: bold;

	text-align: left;

	padding-left: 20px;

}

span.gender_male {

	color: white;

	font-weight: bold;

	font-size: 12px;

}

span.gender_female {

	color: pink;

	font-weight: bold;

	font-size: 12px;

}
		span.gender_omni {
			color: white;
			font-weight: bold;
			font-size: 12px;
		}

.green {

	color: #d6fffc;

}

.lightgrey {

	color: #FFFFFF;



}

.bold {

	font-weight: bold;

}

span.label {

	color: #FFFFFF;




	font-weight: bold;

	font-size: 13px;

}

.tiny {

	font-size: 9px;

}

.med {

	font-size: 12px;

}

.large {

	font-size: 14px;

}

.label2 {

	color:#FFFFFF;

	font-weight:bold;

	font-size:12px;

}

span.post {

	font-size: 12px;

}

span.post a {

	font-weight: bold;

}

td.breathe {

	width: 120px;

}

td.indent, th.indent {

	padding-left: 20px;

}

td.dim {

	background-color: #161616;

}

.color1 {

	background-color: #161616;

}

.color2 {

	background-color: #161616;

}

.color3 {

	background-color: #161616;

}

input.num {

	width: 50px;

	text-align: center;

}

.tooltip {

	position: absolute;

	background-color: #161616;

	padding: 3px;

	padding-left: 10px;

	font-weight: bold;

	color: #efdecc;

	font-size: 12px;

	opacity: .9;

	text-align: left;

}

.fade {

	opacity: .10;

	filter: alpha(opacity=10);

}





table.thin {

	border-spacing: 1px;

	width: 100%;


}



table.thin td, table.thin th {

}



table.hold td.color3, table.widehold td.color3, td.color3 {
background-color: #000000;
}


table.hold td, table.widehold td {

	background-color: transparent;

	padding: 0px;

	border: none;

}

table.hold {

	width: auto;

}

table.widehold {

	width: 100%;

}







#layout_nav_plate_text a,#layout_nav_plate_text a:hover  {

	color: #FFFFFF;


	font-weight: bold;

	font-size: 12px;

	text-decoration:none;

}











#layout_scene {

	margin-left:auto;

	margin-right:auto;

	margin-top:14px;

	background-repeat: no-repeat;

	background-position: -1px -1px;

	width: 501px;

	height: 118px;

}





.layout_button_expand {

	position:absolute;

	top: 0px;

	width:144px;

	height:27px;

	overflow:hidden;

	background-image:url(http://oi42.tinypic.com/2w5lxzl.jpg);

	background-repeat:y-repeat;

}

#layout_button1_expand {

	left: 0px;

}

#layout_button2_expand {

	left: 144px;

}



#layout_button3_expand {

	left: 288px;

}

#layout_button4_expand {

	left: 432px;

}

#layout_button5_expand {

	left: 576px;

}

.layout_button_content {

	color:white;

	padding:27px 10px 10px 5px;

	text-align:left;

}



.layout_button_content a, .layout_button_content a:visited, .layout_button_content a:hover {

	color:#efdecc;

	font-size:11px;

	text-decoration: line-through;

	font-weight: bold;

}



.layout_button_content a:hover {

	text-decoration: line-through;

}



.layout_button_head {

	position:absolute;

	left:0px;

	top:0px;

	background-image:url(http://oi42.tinypic.com/2rw17c7.jpg);

	width:144px;

	height:17px;

	background-repeat:no-repeat;

}



.layout_button_foot {

	position:absolute;

	left:0px;

	bottom:0px;

	background-image:url(http://felisfire.com/images/layout_white/button_foot.png);

	width:144px;

	height:13px;

	background-repeat:no-repeat;

}



.layout_button {

	cursor:pointer;

	position:absolute;

	left:0px;

	top:0px;

	width:144px;

	height:27px;

	background-repeat:no-repeat;

}

#layout_button1 {

	background-image:url(http://felisfire.com/images/layout_white/button_laboratory.png);

}

#layout_button2 {

	background-image:url(http://felisfire.com/images/layout_white/button_world.png);

}

#layout_button3 {

	background-image:url(http://felisfire.com/images/layout_white/button_games.png);

}

#layout_button4 {

	background-image:url(http://felisfire.com/images/layout_white/button_interact.png);

}

#layout_button5 {

	background-image:url(http://felisfire.com/images/layout_white/button_helpinfo.png);

}

.ui-timepicker-div .ui-widget-header { margin-bottom: 8px; }
.ui-timepicker-div dl { text-align: left; }
.ui-timepicker-div dl dt { height: 25px; margin-bottom: -25px; }
.ui-timepicker-div dl dd { margin: 0 10px 10px 65px; }
.ui-timepicker-div td { font-size: 90%; }
.ui-tpicker-grid-label { background: none; border: none; margin: 0; padding: 0; }

.ui-timepicker-rtl{ direction: rtl; }
.ui-timepicker-rtl dl { text-align: right; }
.ui-timepicker-rtl dl dd { margin: 0 65px 10px 10px; }
}
