css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com") {
#global_header {
    position: fixed;
    right: 0;
    left: 0;
    z-index: 1030;
    margin-bottom: 0;
}
body .maincontent {
    padding-top:85px;
}

.grouppage_content_area .maincontent {
  padding-top:0px;
}
.community_home_header {
  padding-top:125px;
}
.no_header {
  padding-top:85px;
}
div#global_header div.logo {
  padding-top:16px;
}
div#global_header .content {
  height: 70px;
}
div#global_header .menuitem {
  padding-top: 25px;
}
div#global_header .menuitem_new {
  top:11px;
}
.header_installsteam_btn {
  display:none;
}

div#global_actions .user_avatar {
  padding-top:10px;
}

span#account_pulldown, span#account_pulldown.focus {
  margin-top:18px;
  padding-left:7px;
}

.header_notification_btn {
  margin-top: 19px;
}
.header_notification_empty {
  display:none;
}
.joinchat_bg, .grouppage_join_area {
  display:none;
}
.membercounts {
  margin-bottom:0px;
}
.grouppage_friendsingroup .friendavatar_link {
  display:none;
}
.grouppage_friendsingroup {
  top:69px;
}
body, .pagecontent {
  background-position: 0 70px;
}
#ig_header {
  margin-top:0px;
  padding-top:100px;
}
.block a[href="http://store.steampowered.com/mobile/?snr=1_4_4_"],
.block a[href="http://store.steampowered.com/bigpicture/?snr=1_4_4_"] {
display:none;
}
#about_header_area {
  padding-top:85px;
}
#enhanced_pulldown {
  position:absolute;
  left:93px;
  font-size:0px;
  padding-top:3px;
  margin-top:-3px;
}
#enhanced_pulldown:after {
  content: "ES";
  font-size:11px;
}
#es_wallet {
  position:absolute;
  left:63px;
  top:3px;
}
#BG_top {
  position: fixed;
  right: 0;
  left: 0;
  z-index: 1029;
  padding-top:30px;
  margin-top:-14px;
  background-color:#1F1F1F;
}
#BG_top .user_info {
  margin-top:23px;
}

#BG_bottom {
  padding-top:112px
}
}
