/*Begin default style */
/*#6BADF6*/

/*BROWSER BORDER*/
#browser-border-start,
#browser-border-end {
    display: none !important;}
#main-window[sizemode="normal"] #browser,
#browser-bottombox {
    border-left: 1px solid rgba(218,218,218,100) !important;
    border-right: 1px solid rgba(218,218,218,100) !important;
    border-color: rgba(218,218,218,100) !important;}
