/* App menu button */
#appmenu-button:before {
    display:none !important;
}
#appmenu-button {
    transition:all 0.1s;
    -moz-padding:0px !important;
    padding:0px !important;
    box-shadow:none !important;
    background-color:transparent !important;
    background-image:none !important;
    border-radius:0 !important;
    border:none !important;
    text-shadow:none !important;
    text-transform:uppercase !important;
    font-weight:normal !important;
}

#main-window #appmenu-button .button-menu-dropmarker:after {
    background-color:#FF7901 !important;
    content: "Firefox" !important;
    text-align:middle !important;
    padding: 7px 15px !important;
}
#main-window #appmenu-button .button-text {display:none !important}

#main-window #appmenu-button .dropmarker-icon {display:none !important;}
/* Toolbars and toolbar buttons*/
#nav-bar , #addon-bar , #PersonalToolbar , #FindToolbar {
    background-image:none !important;
    background-color:rgba(255,255,255,0.8) !important;
    padding:2px 5px !important;
    border-color:transparent !important;
}

#addon-bar , #FindToolbar {
    border:none !important;
} 

#unified-back-forward-button #back-button .toolbarbutton-icon,
#unified-back-forward-button #forward-button .toolbarbutton-icon{
    background: none !important;
    box-shadow: 0px 0px 0px transparent;
    transition: none !important;
    background-clip: padding-box !important;
    border:1px solid transparent !important;
    border-radius:2px !important;
    margin:0 !important;
    transition:all 0.2s !important;

}
