css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("codecademy.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset, header

{
color: white !important;
background: black !important;
}

select, button, .lesson-left-bar__header, .lesson-action-bar__inner, .lesson-checkpoint__inner, .lesson-checkpoint__instructions, .block-label--light-blue, .content, .wrapper, .curricula__item--hotdog__content, .curricula, .curricula__gallery, .curricula__item__section-main, .curricula__more-content, .curricula__more-content__inset, .cmpsr-modal div, .dashboard__latest-goal, .in-progress-goal__container, .latest-goal, .frame-card__metadata, .track-progress, .curricula__item__section-logo, .curricula__item__section-start, .course-item__info, .curricula__item--featured, .curricula__hero-banner, .full-width-container, #homepage-super-footer, .in-progress-goal__preview, .latest-goal__preview, .lesson-nav div

{
background: black !important;
}

span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option, .ace_comment

{
color: white !important;
}


/*links*/

a, .light-text

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

#homepage-super-footer

{
border-top: 9px solid #000000 !important;
}

}
