css:
@-moz-document domain("www.mpcforum.pl"){
body {
background-color:#141414 !important;
}

#branding {
background-image:none !important;
margin-top:15px !important;
}

#search {
margin-top:55px !important;
}

#search_wrap {
box-shadow:0 0 20px 1px #000 !important;
}

#search .submit_input {
background-color:#000 !important;
border-color:#BDBDBD !important;
}

#search .submit_input:hover,#search .submit_input:focus {
background-color:grey !important;
}

#userbar,#user_navigation,#user_navigation a, .ipsList_inline li:first-child, #trade_menu {
border:0 !important;
}

#secondary_navigation {
background-color:#161616 !important;
background-image:none !important;
border-radius:0 !important;
}

#idm_category .idm_category_row,.store_categories li,#idm_categories a,.ipsList_data li,.ipsList_withminiphoto li,table.ipb_table tr td, .topic_buttons li.important a {
background-image:none  !important;
}

#nav_wrap {
background-color:#000 !important;
border:0 !important;
box-shadow:0 0 20px 5px #000 !important;
left:0 !important;
position:fixed !important;
top:0 !important;
width:100% !important;
z-index:9999999999 !important;
}

#nav_wrap li a {
border-radius:0 !important;
}

#community_app_menu > li > a {
-moz-transition:color .3s ease-in !important;
-webkit-transition:color .3s ease-in !important;
background-color:#000 !important;
border:0 !important;
transition:color .3s ease-in !important;
}

#community_app_menu > li > a:hover {
background-color:#000 !important;
border:0 !important;
}

#community_app_menu > li.active a {
background-color:#000 !important;
border:0 !important;
color:#FFF !important;
font-weight:bold !important;
}

#primary_nav {
padding:0 !important;
}

.content_border {
border:1px solid #1a1a1a !important;
}

#row2 announcement {
background-color:#202020 !important;
}

.entry.featured .entry_header,.entry.featured .entry_footer {
background-color:#3c3c3c !important;
}

.maintitle {
background-color:#212121 !important;
background-image:none !important;
border-bottom:0 !important;
border-radius:0 !important;
margin:0 !important;
}

.border {
border-top:0 !important;
}

.post_block h3 {
background-color:#303030 !important;
background-image:none !important;
}

.post_body,#forum_footer,.ipsBox_container {
background-color:#1a1a1a !important;
}

.pagination .page a,.pagination .back a,.pagination .forward a {
background:#1b1717 !important;
}

.ipb_table[summary~=Fora] tbody tr:hover {
background-color:#222 !important;
}

table.ipb_table[summary~=Fora] td {
  padding: 0 10px !important;
}

.column_view .post_wrap, .border, .statistics, .post_block, .ipsComment, .popupInner, .no_messages, .poll_question ol, .ipsBox_container, .ipsFloatingAction, .column_view .post_body, .ipsSideBlock, .general_box {
background:#181818 !important;
}

#secondary_navigation #breadcrumb li span,#secondary_navigation #breadcrumb li a {
background-image:none !important;
color:#BDBDBD !important;
transition:color .3s ease-in !important;
}

.pagination .pages li.active,.ipsBadge,.topic_buttons li.important a,.topic_buttons li.important span,.ipsButton .important,.topic_buttons li a,.topic_buttons li span,.ipsButton,ul.post_controls a,.ipsLikeButton.ipsLikeButton_enabled,.reputation.positive,.members li.positive,.message,.ipsButton_secondary,.bbc_spoiler_show,.user_controls li a,.input_submit, .input_submit.alt {
background-color:#212121 !important;
background-image:none !important;
}

ul.post_controls li.multiquote.selected a {
background-color: rgb(121, 121, 121) !important;
background-image: none !important;
}

.message {
border-color: #393939 !important;
}

.pagination .pages li.active:hover,.ipsBadge:hover,.topic_buttons li.important a:hover,.topic_buttons li.important span:hover,.ipsButton .important:hover,.ipsButton:hover,.topic_buttons li a:hover,ul.post_controls a:hover,.ipsLikeButton.ipsLikeButton_enabled:hover,.ipsButton_secondary,.bbc_spoiler_show,.user_controls li a:hover,.input_submit:hover {
background-color:#505050 !important;
}

.ipsLikeButton.ipsLikeButton_enabled:before {
content:"âœ” " !important;
}

.ipsLikeButton.ipsLikeButton_enabled {
padding-left:7px !important;
}

#secondary_navigation #breadcrumb li span:hover,#secondary_navigation #breadcrumb li a:hover {
color:#FFF !important;
}

p.citation, .ipsLikeButton.ipsLikeButton_disabled {
background-color: rgb(32, 32, 32) !important;
padding: 2px 7px  !important;
background-image:none !important;
}

.ipsLikeBar_info {
background-color: rgb(48, 46, 46) !important;
}

.ipsLikeButton.ipsLikeButton_disabled:hover {
background-color: #212121 !important;
}

.ipsLikeButton.ipsLikeButton_disabled:before {
content: "X " !important;
}

blockquote.ipsBlockquote {
background:#1a1a1a !important;
}

a {
color:#b1b1b1 !important;
}

a:hover {
color:#FFF !important;
transition:color .3s ease-in !important;
}

#footer_utilities {
background-color:#1a1a1a !important;
background-image:none !important;
}

.ipsSideBlock,.row1,.row2 {
background-color:#1a1919 !important;
}

.ipsFilterbar,.custom_fields .ft,.custom_fields .fc,.ipsAd,#copyright,.skin_link, #secondary_links, .col_c_forum .desc,.col_c_forum .__forum_desc ipsType_small,a[id=cab_awards], .guestMessage, #cookie_box {
display:none !important;
}}
