css:
@-moz-document domain(tumblr.com) {
#logo {
	zoom: 0.7;
}
#name, #kb_board_rss {
	margin: 3px 10px;
}
#seach {
	margin: 0;
}
#kb_search {
	zoom: 0.9;
	margin-top: -4px;
}
#view, #addCard-button {
	margin: 0 3px;
}
#icons {
	margin: 0;
}
#toolbar {
	margin: 0;
	height: auto;
}
#kb-board {
	margin-top: 31px;
	margin-left: 0;
}
.allLaneHeader, ul.laneContents {
	padding: 0;
	margin: 1px 0 0 1px;
}
.kb-card-icon {
	zoom: 0.5;
	margin: 15px 15px 0 0;
}
.kb-size-display {
	border: none;
	font-size: 1em;
	min-width: 0;
	padding: 1px;
	margin: 0;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	-ms-box-shadow: none;
	-o-box-shadow: none;
	box-shadow: none;
	-ms-filter: "progid:DXImageTransform.Microsoft.Shadow(Strength=0,Direction=0,Color='#666666')";
}
.kb-card-sidebar {
	height: auto;
	position: absolute;
	margin: 0 1px 0 0;
	right: 0;
	bottom: 0;
	top: auto !important;
}
.lane {
	height: auto !important;
}
.text {
	width: auto;
	padding: 4px;
}
.card {
	margin: 4px 10px 4px 8px;
}
li.card .card-background {
	-webkit-box-shadow: 1px 1px 1px rgba(128,128,128,0.5);
	-moz-box-shadow: 1px 1px 1px rgba(128,128,128,0.5);
	-ms-box-shadow: 1px 1px 1px rgba(128,128,128,0.5);
	-o-box-shadow: 1px 1px 1px rgba(128,128,128,0.5);
	box-shadow: 1px 1px 1px rgba(128,128,128,0.5);
	-ms-filter: "progid:DXImageTransform.Microsoft.Shadow(Strength=4,Direction=135,Color='#666666')";
}
.kb-avatar {
	opacity: 0.2;
	-webkit-transition: all 500ms;
	-moz-transition: all 500ms;
	-ms-transition: all 500ms;
	-o-transition: all 500ms;
	transition: all 500ms;
	
}
.card:hover .kb-avatar {
	opacity: 1;
}
#growlDock {
    -webkit-transition: all 200ms;
	-moz-transition: all 200ms;
	-ms-transition: all 200ms;
	-o-transition: all 200ms;
	transition: all 200ms;
    left: 0px !important;
    padding-left: 10px !important;
}

#kb-board:hover ~ #growlDock {
    left: -300px !important;
}

#growlDock:hover {
    left: 0px !important;
}
}
