css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhidao.baidu.com") {

div#union-qbbanner,
div[class="line mt-10 app-and-share"],
div#cms-slide,
div.cms-slide,
div#task-list,
div[class^="uc-enter-btn mt-10"],
div#union-qbrightdown
{
    display: none !important;
}

div#wgt-user
{
    height: 77px !important;
    width: auto !important;
}

aside#qb-side
{
    float: right !important;
    position: absolute !important;
    top: 18px !important;
    right: 0px !important;
}

div#signInColumn
{
    margin-left: 0px !important;
    right: 0px !important;
    top: -20px !important;
}

article#qb-content
{
    width: 978px !important;
}

[class^="line mt-10 q-content"],
[class^="line mt-10 q-supply-content"],
[id^="best-content-"],
[id^="answer-content-"],
[accuse^="aRA"],
[accuse^="qRA"]
{
    font-family: 'Ubuntu Mono','DejaVu Sans Mono','Lucida Sans Typewriter','Consolas','Monospace','Courier New','abook','QingYuan' !important;
}

/* key */
[class*='title'],
h1,h2,h3,h4,h5,h6,h7,h8,h9,
A#user-name.user-name,
/* bold */
b,
strong
{
    font-family: 'Ubuntu Mono','DejaVu Sans Mono','Lucida Sans Typewriter','Consolas','Monospace','Courier New','abook','QingYuan' !important;
    font-weight: normal !important;
    color: RGB(0, 144, 32) !important;
    text-shadow: 0px 0px 0.25em rgb(160, 160, 160) !important;
}

}
