css:
@-moz-document url-prefix("http://www.reddit.com"), domain("reddit.com") {
@keyframes intensifies{
    0%{left:0px;top:0px;}
    10%{left:1px;top:0px;}
    20%{left:0px;top:1px;}
    30%{left:-1px;top:0px;}
    40%{left:0px;top:1px;}
    50%{left:1px;top:0px;}
    60%{left:1px;top:-1px;}
    70%{left:1px;top:1px;}
    80%{left:1px;top:-1px;}
    90%{left:-1px;top:1px;}
    100%{left:0px;top:0px;}
}

@-webkit-keyframes intensifies{
    0%{left:0px;top:0px;}
    10%{left:1px;top:0px;}
    20%{left:0px;top:1px;}
    30%{left:-1px;top:0px;}
    40%{left:0px;top:1px;}
    50%{left:1px;top:0px;}
    60%{left:1px;top:-1px;}
    70%{left:1px;top:1px;}
    80%{left:1px;top:-1px;}
    90%{left:-1px;top:1px;}
    100%{left:0px;top:0px;}
}

a[href$="-intensifies"] {
    position: relative;
    animation: intensifies .2s linear 0s infinite alternate !important;
    -webkit-animation: intensifies .2s linear 0s infinite alternate !important;
}
}
