css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news2plus.blog123.fc2.com") {

*{
font-weight:normal !important;
font-size:1em !important;
line-height:1.6em !important;
}

#baseBlock br,
#menuBlock,
#BlockRight,
.SbBlock,
#mainBlock iframe,
.mainEntryBlock ol,
#FootBlock,
.pageLink + .mainEntryBlock + .pageLink,
.mainEntryBlock + .pageLink + .mainEntryBlock,
.mainEntryMore > div + div,
img[src="http://img.addclips.org/addclips_1.gif"],
a[href="http://fc2.com/"]{
display:none !important;
}

#baseBlock,
#headBlockUnder,
#BlockLeft,
#mainBlock,
.mainEntryBase,
.mainEntryTitle,
.mainEntryBlock{
width:auto !important;
float:none !important;
}

#baseBlock{
margin-left:1em !important;
margin-right:1em !important;
}

#headBlockUnder br {display:none;}
.headBlogName {font-size:1.4em !important;}

#headBlockUnder {
height:auto !important;
}

.mainEntryMore {
border-top:none !important;
}
.mainEntryBody,
.mainEntryMore{
font-size:0.8em !important;
}

.mainEntryBody .Bb,
.mainEntryMore .Bb{
font-size:1.25em !important;
}

.Bb img{
display:block;
}

.pageLink{
font-size:0.9em !important;
width:auto !important;
}

}
