css:
@-moz-document url-prefix("https://play.google.com/music") {

	div.bio.collapsed, .album-view .description {
		overflow-y: hidden !important;
		font-size: 1em !important;
		line-height: 1em !important;
		font-weight: normal !important;
	}


	div.bio.collapsed:hover, .album-view .description:hover {
		overflow-y: auto !important;
		font-size: 1.3em !important;
		line-height: 1.3em !important;
		font-weight: normal !important;
	}

	div.read-more-button-wrapper.fade-out.gray {
		display: none !important;
		/* REMOVE LINE ABOVE IF YOU WANT THE "Read More" BUTTON TO STILL APPEAR (AT VERY BOTTOM) */
  		position: relative !important;
 	 	padding: 20px !important;
		left: 0 !important;
		bottom: 0 !important;
	}

}
