css:
@-moz-document domain("yammer.com") {
body, .page-content {
    font-family:Helvetica,Arial,sans-serif !important;
    font-size:inherit;
    background-color:whiteSmoke !important;
}
#masthead-container {
    width:60px !important;
    height:60px !important;
    border-bottom:none !important;
    background-image:none !important;
    background-color:#048abb !important;
    min-width:60px !important;
    position:fixed !important;
    top:0;
    left:20px;
    border-radius:0 0 50% 50%;
  	-webkit-box-shadow: 0 1px 2px rgba(0,0,0,0.2);
}
#masthead .network-logo-img {
    vertical-align:top !important;
    padding:10px;
}
#uni-header-background { background-image:-webkit-gradient(linear, left top, left bottom, color-stop(0, white), color-stop(1, #eee))!important;}

.badge[style],
#column-three,
.yj-follow-button-tiny,
.yj-chatmanager,
.yj-thread-topics-container {
    display:none !important;
}
.two-column-layout .column-two-left {
    width:780px !important;
}
#uni-header-container #uni-header {
    margin-top:1px !important;
}
.yj-main-mugshot img,
.yj-avatar img,
.yj-reply-avatar img {
    border-radius:50%;
}
.yj-tab .yj-menu-icon, .yj-tab .yj-label { opacity:0.5; }
.yj-tab .yj-selected .yj-menu-icon,
.yj-tab .yj-selected .yj-label,
.yj-tab .yj-item:hover .yj-menu-icon,
.yj-tab .yj-item:hover .yj-label { opacity:1; }
.yj-file-attachment-list-item {
	width:49% !important;
}
img[src$=".gif?tracking=false"] {
	display:block;
}
.yj-to-label-wrap .yj-to-label { font-size:11px !important;opacity:0.5; }

.yj-thread-starter .yj-message-body .yj-message {
    font-size:14px !important;
    line-height:20px !important;
}

div.three-column-layout, .two-column-layout { background-image:none !important; }

.yj-nav-menu .nav-list-link { border:none !important;border-radius:3px !important;}
.yj-nav-menu .yj-selected .nav-list-link { background:none !important;}
.yj-nav-menu .nav-list-link:hover { background:#efefef !important;color:#003f93 !important;}

.yj-thread-list-item {
    background-color:#fff !important;
    padding:25px 50px 25px 25px !important;
    margin-bottom:20px !important;
    border:1px solid #eee !important;
    border-radius:5px !important;
}

#yj-feedToggle {
    margin-bottom:10px !important;
    border-bottom-style:none !important;
}

.yj-thread-starter-likes,
.yj-show-newer-replies,
.yj-unread-show,
.yj-show-older-replies,
.yj-thread-reply-container,
.yj-thread-reply-list-item {
    background-color:white !important;
    border-bottom:1px dotted #ddd !important;
    margin-bottom:0 !important;
}

.yj-likes-text { background-color:white !important;}
.yj-thread-reply-container { border-bottom:0 !important;}
.yj-publisher-textarea { border-color: #ddd !important;}
.page-header { border:none !important;}

.yj-selected a { 
    background-color:whiteSmoke !important;
    border-bottom-color:whiteSmoke !important;
}

.yj-feed-filter .yj-click-menu-trigger {background-color:whiteSmoke !important;}
}
