css:
@-moz-document

url-prefix("http://www.tumblr.com/following"), url("http://www.tumblr.com/likes"),
url("http://www.tumblr.com/dashboard/"), url-prefix("http://www.tumblr.com/blog/"),
url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/tagged/"), 
url-prefix("http://www.tumblr.com/spotlight/"), url-prefix("http://www.tumblr.com/edit/"), 
url-prefix("http://www.tumblr.com/liked/by/") , url-prefix("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/"), url-prefix("https://www.tumblr.com/liked/by/") {


body { background:#161319 !important; }

#left_column,#right_column,#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:#161319 !important;
}

#posts .post, .post_media, post_content, .post_content_inner, .video_embed, .photoset.photo, img, image, img.img, div.search_form_row,.clearfix  {
    background:#212024 !important; 
    color:#999!important;
    text-shadow:0px 0px 0px rgb(19, 43, 66)!important;
}

#posts .post a, .post_media a, post_content a, .post_content_inner a, span.quote, .follower a, .unfollow_button  {
    color:#9ac!important;
}

.post_footer {
    border-top:0px !important;
}

div.note_item {
    background:#222 !important;
    border: 1px solid #333 !important;
    color:#bbb!important;
}

div.nipple, div.selection_nipple {
    visibility: hidden !important;
}

#auto_pagination_loader {
    background: #161319 !important;
    color:#999 !important;
    text-shadow: 0px 0px 0px #111 !important;
    border-top: 0px !important;
}

.white_pane {
    border:0px solid #222 !important;
    box-shadow: 0px 0px 24px #222 inset !important;

    padding:12px !important;
}

.white_pane, .follower {
    background: #333 !important;
    color:fff !important;
}

.white_pane h1, .white_pane a{
    color:#999 !important;
}



div.follower {
    padding:3px !important;
}

div.link_text_container {
    background: #282535 !important;
    color:fff  !important;
    border:0px !important;
}

div.link_text_container a {
    color:#999 !important;
}

.dashboard_iframe,.post_full .post_tags:after {
    background:transparent!important;
}

.post_full .post_permalink {
    background:url(http://i.imgur.com/Ixdsz0r.png)!important;
}


}
