css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vim.wikia.com") {
    a:link{
        text-decoration:none !important;
        color:#626249 !important;
    }
    a:hover {
        text-decoration:underline !important;
        color:#007F00 !important;
    }
    a:visited {
        color:#8A8A67 !important;
    }
    li.nav-item.marked a,
    a.subnav-2a,
    a.wikia-button,
    a#ca-viewsource {
        color:white !important;
    }
    li.nav-item.marked a:hover,
    a.subnav-2a:hover,
    a.wikia-button:hover,
    a#ca-viewsource:hover{
        text-decoration:underline !important;
    }
    a.wikia-button.comments.secondary.talk,
    a.wikia-button.share-button.secondary {
        color:black !important;
    }
    a.wikia-button.comments.secondary.talk:hover,
    a.wikia-button.share-button.secondary:hover {
        text-decoration:underline !important;
    }

}
