css:
@-moz-document domain(myspace.com) {

body, body * {
  background: #fff !important;
  color: #000 !important;
  border-color: #ccc !important;
}

body * a {
  color: blue !important;
}

object, embed, iframe {
  display:none !important;
  visibility: collapse !important;
}

img {
  max-width: 300px !important;
}
#caption{
 background: none !important;
}

input {
  z-index: 1000 !important;
}

body table tbody tr td table tbody tr td.text table tbody tr td table tbody tr td div aq, body table tbody tr td table tbody tr td.text table tbody tr td table tbody tr td b a {
  display: none !important;
}

.contactTable a img {
  visibility:visible !important;
}

body * {
  filter:alpha(opacity=100) !important;
  -moz-opacity:1 !important;
  opacity:1 !important; 
  -khtml-opacity:1 !important; 
}

#header {
  height: auto !important;
}
table{
border:solid black ;
}
td{
word-wrap:  break-word;
}
.friendSpace {
display: block !important;
visibility: visible !important;
}


}
