css:
@-moz-document domain("dev.java.net"), domain("download.java.net"), domain("docs.oracle.com") {
div#banner, div#toptabs {padding:0px !important;}
div#banner img[width="500"][height="60"] {height:20px !important;}
table#donate {display:none !important;}
body, .FrameHeadingFont, .FrameItemFont {
  font-family:  'Arial Unicode MS', 'Segoe UI', 'Droid Sans', 'Franklin Gothic', Verdana, 'Franklin Gothic Medium', Arial, sans-serif !important;
  font-size: 16px !important;
  line-height: 24px !important;
  text-shadow: 2px 5px 6px rgba(0,0,0,0.2), 0px -5px 35px rgba(255,255,255,0.3);
  background-color: #eee !important;
}
pre, code, tt {
  font-family: 'Envy Code R', 'Segoe UI Mono', Consolas, Monaco, 'Source Code Pro Semibold', Menlo, 'Courier New', monospace !important;
  font-size: 13px !important;
}
pre > code {
  line-height: 1.5;
}
pre {
  border-radius:4px;
  padding: 8px;
  background-color: #000;
  text-shadow: none;
  color: #eee;
}
pre a, pre a:link, pre a:visited {
  color: #8ABAE6;
}
pre a:hover, pre a:focus {
  color: #EEB36B;
}
.contentContainer, body[onload] {
  width: 666px;
}
table {
  border: none;
}
table td, table th {
  border: none;
}
table:not(.overviewSummary) tr:nth-child(2n) {
  background-color: #eee;
}
caption span {
  padding-top: 4px !important;
  padding-bottom: 8px !important;
}

}
