css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  {

.new_post_label .new_post_label_icon {
	background-image: 
  url(http://s16.postimg.org/5h3koajl1/post_icons_sprite.png);
}


.small_links {
  	display: none !important;
}

.controls_section:not(.blog_menu), .small_links {
	top: 320px;
}
         
.dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .post_full .post_avatar .queue .publish_info.day, .post_full .post_avatar .queue .publish_info.time {
    color: #fff !important;
}

.dashboard_options_form {
    border: none;
	color: inherit;
}


#tumblr_radar, .sidebar_nav.clearfix, .controls_section:not(.blog_menu) > :last-child {; 
  display: visible;
}

.post, .notification, #new_post, .controls_section {
	background-color: #191818;
    color: whitesmoke;
}
    
.post_content.clearfix, .post_content.clearfix a, #new_post a {
	color: inherit;
	background-color: inherit;
}
      
.post_avatar:after {
	width: 0px;
}
      
#right_column:after {
	background: none;        
}

body {
background-image: 
  url(http://s1.directupload.net/images/140127/qbfocnzt.png);
  background-attachment: fixed; 
  background-color: #0e0e0e !important;

}

.count, #open_blog_link, .controls {
	filter: grayscale(1);
    -webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1);
}
  
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: #121212;    
}
  
.post_permalink {
    background: url('data:image/png;base64,') !important;
}

.selection_nipple {
    display: none !important;
}
      
.post_title {
	color: inherit !important;
}
    
.plexi.show.color {
    background: rgba(0, 0, 0, 0.71);
}

.form_wrapper + .plexi.show {
  display: none !important;
}

.ui_dialog_lock.opaque {
background: rgba(0, 0, 0, 0.7);
}

#post_controls, #track_tag_button {
	filter: grayscale(1);
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1);
}
            
.search_popover .result_link:hover, .search_popover .result_link:focus, .search_popover .more_results_link:hover, .search_popover .more_results_link:focus {
    background: rgba(0, 0, 0, 0.3);
}

.search_popover .result_link:hover:before, .search_popover .result_link:focus:before, .search_popover .more_results_link:hover:before, .search_popover .more_results_link:focus:before, .search_popover .result_link:hover:after, .search_popover .result_link:focus:after { background-color: transparent; }
              
#search_query, #unread_tag_notice { 
	color: #a2a2a2;
}


.xkit-reply-button {
      color: #000 !important;
}

.tab_notice_nipple, #new_post_notice_container, .ui_dialog {
    filter: grayscale(1);
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1);
}
    
#tab_switching .tab_notice, div.l-header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
    filter: grayscale(1);  
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1);
}
  
.controls_section .blog, #recommended_tumblelogs {
    display: none;
}

img { 
  	border-radius: 3px; 
}

.ui_dialog_lock.opaque {
  	color: rgba(0, 0, 0, 0.65);
}

.right_column {
    background: transparent;
}
  
.settings_container {
    color: #fff !important;
}
    
.left_column, .tumblelog_name, #fixed_navigation > .vertical_tab {
    color: #fff;
}

.notification .avatar_frame, .ui_avatar .frame, .notification .notification_right .preview_frame {
    border: 1px solid #000;
}
    
.post_avatar {
    background-color: transparent
}

.link_text_outer {
    color: black;
}

#auto_pagination_loader {
  	background: #191818 none;
  	text-shadow: #000;
}

.spinner {
    filter: grayscale(1);
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1);
}

.post_full.is_conversation .conversation_lines .chat_line:nth-child(even) {
    background: #3F3F3F;
}

.main_content input[type="text"], .main_content textarea {
    color: #B1B1B1;
}

.post_full .post_body pre {
  	background: inherit;
}

.ui_note.is_follower .part_icon .note_follow, .notification .notification_right .preview_frame.conversation, .ui_post_badge.conversation, #toaster .icon.conversation {
    filter: grayscale(1);
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1); 
}

.ui_notes .ui_note.is_friend {
    background-color: #E6E6E6;
}

.ui_notes .ui_note.is_friend:hover, .ui_notes .ui_note:hover {
  	background-color: #C4C4C4
}
              
#right_column .small_links {
    position: fixed;
   	top: 290px;
    box-sizing: border-box;
    width: 213px;
}

.notifications_page .section.divider {
    background: transparent url(http://img856.imageshack.us/img856/5812/xwz4.png) 0 bottom no-repeat;
}

#dashboard_controls {
	filter: grayscale(1);
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
	-o-filter: grayscale(1);
}
  	
.top_notes, .xkit-reply-button {
    color: #000;
}

#toaster .frame {
	border: 1px solid #000000;
}
.post_full .post_controls:after {
    background: url(http://img607.imageshack.us/img607/3103/8lgv.png) no-repeat;
}
      
.post .post_info .reblog_icon {
    background: -397px -93px url(http://img607.imageshack.us/img607/3103/8lgv.png) no-repeat;
}
.dashboard_header {
    color: rgb(151, 151, 151) !important;
}
  
.dashboard_header a {
    color: white !important;
}
.post_micro .post_content {
color: black;
}

p.asker + p {
color: black;
}

.cog {
    background: url(http://img266.imageshack.us/img266/4545/w3x2.png) -40px 0 no-repeat;
}

.share_btns label {
    background: url(http://img266.imageshack.us/img266/4545/w3x2.png) -120px 0 no-repeat !important;
}

#posts .post .post_control.photo_reply .photo_reply_icon_base {
  background: url(http://img266.imageshack.us/img266/4545/w3x2.png) no-repeat scroll -501px -327px transparent !important;
}

.xpostblockbutton:after {
background-image: url('data:image/png;base64,') !important;
}
.chrome.blue {
    filter: grayscale(1);
  	-webkit-filter: grayscale(1);
	-moz-filter: grayscale(1);
    -o-filter: grayscale(1); 
}
.share_btn.facebook label {
background: url(http://img266.imageshack.us/img266/4545/w3x2.png) -160px 0 no-repeat !important;
}

.share_btn.twitter label {
background: url(http://img266.imageshack.us/img266/4545/w3x2.png) -120px 0 no-repeat !important;
}

.settings_container.first {
border-top: 1px solid #d7d7d7;}
.settings_container {
border-bottom: 1px solid #d7d7d7;}

.settings_container.first {
border-top: 1px solid #d7d7d7;}
.settings_container {
border-bottom: 1px solid #d7d7d7;
border-radius: 10px;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #000000;
border-bottom: 1px solid #000000;
border-radius: 10px;
}

.post_full .post_footer {background:none!important;}
.search_form_row {background-color:#191818!important;}
.post_full .post_tags{overflow:hidden;}	
.post_avatar:after {border-right-color:#191818;}

.popover_blogs {color:#191818;}

.controls_section{border-radius: 7px;}
}
