css:
@-moz-document domain("wikipedia.org") {

body, #mw-page-base {
background-color: #484848 !important;
background-image: none !important;
}

div#content a {
color: #000000!important;
border-bottom: dotted 1px #b0b0b0 !important;
}

div#content a:hover {
text-decoration: none!important;
border-bottom: dotted 1px blue !important;
}

#content .reference a {
border-bottom: none !important;
font-size: 0.8em!important;
}

#content .reference a:hover {
color: blue!important;
}

#mw-head {
max-width: 76.7em !important;
left: 0px!important;
}

#content {
padding: 5em 4em 4em 4em !important;
box-shadow: 0 1em 3em #101010;
max-width: 60em !important;
}

#mw-content-text p {
font-family: Palatino !important;
font-size: 1.4em !important;
line-height: 1.6em !important;
margin: 1em 0 1.2em 0 !important;
max-width: 28em !important;
}

.thumb.tleft {
margin-right: 3em !important;
}

.thumb.tright, .infobox {
margin-left: 5em !important;
}

#mw-navigation > div {
opacity: 0.5 !important;
-webkit-transition: opacity 0.2s !important;
}

#mw-navigation:hover > div {
opacity: 1!important;
-webkit-transition: opacity 0s;
text-shadow: 1px 1px 1px #303030 !important;
}

div.vectorTabs, div.vectorTabs ul, div.vectorTabs ul li, div.vectorTabs span, div.vectorMenu a{
background-image: none !important;
background-color: transparent !important;
}

div#mw-panel div.portal div.body ul li a, #mw-panel.collapsible-nav .portal.collapsed h3 a, #mw-panel.collapsible-nav, .portal.expanded h3 a,
div.vectorTabs li a,
#mw-head a {
color: #a0a0a0 !important;
}

#mw-panel.collapsible-nav .portal {
background: none!important;
}

}
