css:
@-moz-document
	url-prefix('https://www.tumblr.com/')
{

#right_column .controls_section LI:hover:not(.section_header) {background-color: #000}
DIV.post_avatar {background-color: transparent}

/* blue fix */
/*div.search_form_row*/#search_query {background-color: rgba(0,0,0,0.5)}
/*div.l-header .selection_nipple {border-bottom-color: rgba(100,100,100,0.8)}*/

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

/* fixed header */
DIV.l-header-container { background-color: rgba(0,0,0,.5); }
DIV.l-header-container:hover {background-color: rgba(0,0,0,1)}

BUTTON.compose-button {
	background-color: transparent;
	border-color: #808080;
}
BUTTON.compose-button:before {color: #808080}
BUTTON.compose-button:hover {border-color: #c0c0c0}
BUTTON.compose-button:hover:before {color: #c0c0c0}

/* ======= Background ======= */

BODY:not(.mceContentBody), #new_post.editing {
  background: #090f1b url('/*[[background]]*/') fixed !important;
/*  background-size: auto 300% !important;*/
}
.new_post .title, .new_post .tag_editor {
  background-color: rgba(255,255,255,.8) !important;
}

.l-content {background: transparent !important}





/* ======= Dashboard Style ======= */


#left_column #posts .notification .avatar {opacity:0.7 !important;}
#left_column #posts .notification .avatar:hover{opacity:1 !important;}

/*--#right_column{background-color: #FFFFFF opacity:1 !important;}--*/

#new_post_notice_container{color:#fff!important}


#posts .notification {
	opacity:0.8 !important;
	background:  #163156 !important;
	border: 1px #000000 solid !important;
	margin: -1px;
	}

#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
	border-right: 7px solid #000000 !important;
	}

#posts .notification .nipple, #posts .notification.alt .nipple {
	border-right: 6px solid #163056 !important;
	}





/* ======= Time-cracked Logo ======= */

.logo .svg-logo {display: none}
H1.logo/*.logo-redesign*/ .logo-anchor {height: 100%}

/*#logo {*/
H1.logo/*.logo-redesign*/ {
	width: 210px;
	height: 49px;
/*	margin-top: 2px;*/
	padding: 4px 0 0 4px;
}
/*#logo > .logo_anchor {*/
H1.logo/*.logo-redesign*/ > .logo-anchor {
	display: block;
	background-size: auto;
	background-image: url(http://24.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o1_250.png);
}




/* ======= "Back to Top" Button ======= */

#return_to_top .return_to_top_icon{
background: url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o7_100.png') no-repeat transparent!important}




/* ======= Top Icons ======= */


#popover_blogs .tab_notice:after{display:none; }

.tab.iconic > A.tab_anchor {
	background-image:url('http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o6_250.png');
	background-size: auto;
	width: 35px;
}

  .tab_bar .tab.iconic {padding: 0 6.5px}
  #account_button .icon_user_settings::before,
  DIV.tab_bar > .tab.iconic > .tab_anchor::before { content: none }

  #account_button .icon_arrow_carrot_down {position: relative; right: -24px; }
  #account_button {margin-right: 12px}

  #home_button .tab_anchor { background-position: 0px 0; }
  #inbox_button .tab_anchor { background-position: -35px 0; }
  #xkit-control.xkit-help-forwarder > .tab_anchor,
  /*#help_button*/#activity_button .tab_anchor { background-position: -70px 0; }
  #account_button .tab_anchor { background-position: -105px 0; } /* ex #preferences_button */
  #discover_button .tab_anchor { background-position: -140px 0; } /* ex #logout_button */



/* ======= New Post Icons ======= */


.new_post_label{
font-size:0px !important;
}

/*.new_post_label_icon{background-image:none !important}*/
.new_post_label > I {display: none}

#new_post{
background: #fff url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o10_r1_1280.png) center no-repeat !important;
/*background-color:#fff !important;*/
}





/* ======= Selector ======= */

.selection_nipple.white,
.selection_nipple {
  border: 0px !important;
background-image:url("http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o2_100.png") !important;
/*background:no-repeat url("http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o2_100.png") !important;*/
  width:  36px !important;
  height: 48px !important;
  margin-top: -12px !important;
  background-position: 0px 0px !important;
  background-repeat: no-repeat !important;
}






/* ======= Cursor ======= */

/*--html {cursor: url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o3_100.png), progress;}
a, a:hover {cursor: url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o4_100.png), progress;}--*/
a, input[type=button], input[type=submit], input[type=reset], input[type=file],
.controls_section .sub_control,
.controls_section .open_blog.with_subtitle,
.popover_menu_item:hover,
.popover_menu_item.keyboard_focus,
img[data-full-size] {
  cursor: url(http://25.media.tumblr.com/tumblr_mam3e37V3f1qa0t06o4_100.png), auto !important;
}

}
