css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body{background: url('http://i39.tinypic.com/2ibhwna.png') fixed !important; color:rgba(255,255,255,0.6)!important; font-size:12px !important;}

h1,h2,h3,h4,h5,h6{color:#C2B68D !important}
a, a:visited{color:#C2B68D !important}
a:hover{color:#D4C89C !important}

#next_page_link, #previous_page_link,
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {border:0px!important; background: rgba(211,198,160,0.6) !important;}
.post_question, #left_column #posts .post .permalink {background: rgba(255,255,255,0.6) !important;}

.post_question_nipple, .arrow{background:transparent!important;}#C2B68D

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(242,231,203,0.6) !important;
border-top:0px rgba(242,231,203,0.6)!important;
border-bottom:0px rgba(242,231,203,0.6)!important;
border: 0px solid #4D6780;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs_firt {
background: rgba(242,231,203,0.6) !important;
border-top:0px rgba(242,231,203,0.6)!important;
border-bottom:0px rgba(211,198,160,0.6)!important;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top:0px transparent!important; border-bottom:0px transparent!important;}

li.post, #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(255,255,255,0.15)!important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border:0px!important;
border-radius: 20px;
margin: auto;
padding: 20px 20px;}

#left_column #posts .post .post_avatar,
#left_column #posts .post .sub_avatar .sub_avatar_inner {border-bottom-color: transparent !important;}
#left_column #posts .post{border-bottom:0px #C2B68D solid !important}

#search_controls{border-bottom-color: transparent !important;
border-bottom:none!important; border-top:none!important; border:none!important;
background: rgba(255,255,255,0.6) !important;}

#help_docs:hover,  #all_of_tumblr:hover,
#everyone_i_follow:hover, #my_posts:hover{
background: rgba(211,198,160,0.6) !important;
border-bottom:0px #C2B68D solid !important;
border-top:0px #C2B68D solid !important;}

#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: rgba(211,198,160,0.6) !important;
background-image:none !important;
border:0px !important}

#nav .nav_item .nav_menu {border-bottom:0px !important}
#nav .nav_item .nav_menu:hover{color:#D4C89C !important;background: rgba(255,255,255,0.6)!important;}
#nav .nav_item .nav_menu a {color:#C2B68D !important; border-top-color:0px #fff !important;}
#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {background: none;}

#right_column .dashboard_nav_item {
background: rgba(211,198,160,0.6);
background-attachment: initial;
background-position-x: 50%;
background-position-y: 100%;
background-origin: initial;
background-clip: initial;
background-color: rgba(211,198,160,0.6);}

#photo_upload, #embed_a_video  {background: rgba(211,198,160,0.6) !important;}

#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: rgba(242,231,203,0.6);}

#nav .nav_item .nav_menu a:hover {color:#D4C89C!important;}
#footer, #footer a {color:#DDCE99 !important; background-image:none !important;}

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {color:#fff !important}
#right_column .dashboard_nav_item {border-bottom:0px transparent #fff !important; background-image:none  !important}
#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {color:#C2B68D !important;border-bottom:0px transparent #fff !important;}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
color:#E2BC85 !important;border-bottom:0px transparent #fff !important;}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background: rgba(211,198,160,0.6) !important;
border-bottom:0px transparent #fff !important;}

#show_radar a, #hide_radar a,
#hide_recommended_blogs a, #show_recommended_blogs a {color:#C2B68D!important;}

#left_column #posts .notification .avatar {border-color:transparent !important; background-color:transparent !important;opacity:0.6 !important;}
#left_column #posts .notification .avatar:hover{opacity:0.6 !important;}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(211,198,160,0.6) !important;
}
.dashboard_switch_blog_menu_header{
background: rgba(211,198,160,0.6) !important; color:#DDCE99 !important; border-bottom:0px solid #fff !important;}

.dashboard_switch_blog_menu_item{
background: rgba(211,198,160,0.6) !important;
color:#C2B68D !important;
border-bottom:0px solid #fff !important;}

.dashboard_switch_blog_menu_footer{
background: rgba(211,198,160,0.6) !important; 
color:#C2B68D !important; 
border-bottom:0px solid #fff !important;}

.dashboard_switch_blog_menu_wrapper{
background: rgba(211,198,160,0.6) !important;
border-bottom:0px solid #fff !important;}

#new_post_notice_container{color:#C2B68D !important}
#new_post_notice_container a{color:#C2B68D !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}

#posts .post .reply_pane .nipple,.notification_type_icon{display:none; border:0px;}

#posts li.post.new_post {background: rgba(255,255,255,0.6) !important;}
#nav .nav_item.active {background: rgba(211,198,160,0.6)!important;}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {background: rgba(255,255,255,0.6) !important;}
.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {background: rgba(255,255,255,0.6) !important;}

ol#posts li.notification.first_notification {background: rgba(255,255,255,0.6) !important; border:0px; margin:0px;}
ol#posts li.notification.alt {background: rgba(255,255,255,0.6) !important; border:0px; margin:0px;}

*{text-shadow:none !important; color:#C2B68D !important;}
#content_top, #content_bottom{display:none;}
ol#posts li.notification {background: rgba(255,255,255,0.6) !important; border:0px; margin:0px;}

#left_column #posts .notification a {color:#C2B68D !important; border:0px;}
ol#posts li.notification.last_notification {border:0px;}
#left_column #posts .notification blockquote {border:0px;}

#dashboard_switch_blog_menu{
border:0px!important;
border-top:0px!important;
border:0px!important;
background: rgba(255,255,255,0.6) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;}

.dashboard_switch_blog_menu_item{
border:0px!important;
border-top:0px!important;
border:0px!important;
background: rgba(255,255,255,0.6) !important;}

.user_menu .user_menu_list a .user_menu_list_item,
#dashboard_switch_blog_menu_footer:hover{
background: rgba(255,255,255,0.6) !important;
border-bottom: 0px solid rgba(255, 255, 255, 0.6);
border-top: 0px solid rgba(225, 225, 225, 0.6);}

.user_menu .user_menu_list a .user_menu_list_item:hover{color:#E8E0C7!important;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_footer {border-top:0px!important; border-top-width:0px;}
#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header {
border-bottom:0px solid #fff!important; border-top-width:0px;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header #dashboard_switch_blog_arrow_and_divider {
border-top:0px solid #F6F9FC;}

#posts .notification.single_notification,
#posts .notification.last_notification {border-bottom:0px solid #24394E;}

#posts .notification .reply_pane, #posts .post .reply_pane,
#posts ol.notes li.reblog, #posts ol.notes li.like {background: rgba(255,255,255,0.6) !important; border-bottom:0px;}

#nav .nav_item .nav_menu a,
#search_box #search_controls a {border-bottom: 0px solid #DEE8F1; border-top: 0px solid #FCFDFE;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog .dashboard_controls_suggested_blog_title img {border: 0px solid #677C8F;}
#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {color:#E8E0C7!important;}

}
