css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {

/*--------------------THEME MADE BY spookmycraic.tumblr.com AKA FAYE
DO NOT REDISTRIBUTE
DO NOT USE AS A BASE CODE
DO NOT TAKE PARTS OF MY THEME TO USE
FOLLOW THE RULES OR THERE WILL BE CONSEQUENCES
------------------------*/

body, a, a:hover {

/*cursor credit to 1dcursors.tumblr.com*/
cursor: url('http://i1279.photobucket.com/albums/y538/1dnersx/Ziam_zps3211ae4f.png'), progress !important;

}

body {

  background: #000 url("http://static.tumblr.com/kuehu0n/VINmri5ba/ziam_tca_s_bts.jpg") bottom left fixed no-repeat !important; 
}



.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; 
}



  .help_text, label {
color:#c9c9c9 !important;
}
  .vertical_tab.selected {
color:#000 !important;
        text-shadow: 1px 5px 1px #000000 !important;
}




/* --- SCROLLBAR ---*/
     
::-webkit-scrollbar-thumb{
background-color: #aa7777;
border: 2px solid #ffffff;
height:auto;
}
 
::-webkit-scrollbar {
height:9px;
width:9px;
background-color: #aa7777;
border: 4px solid #fff;
}

::selection {
background-color:#f9f9f9;
}













#search_query {
    background-color:  rgba(0,0,0, 0);
    color: rgb(255,255,255);
}





DIV.l-header-container { /*++*/
	background-color: transparent; /* or maybe pink */
	box-shadow: none; /* else there will be a faint line at the bottom */
	position: absolute; /* let the header move with the page */
}


.l-header > .compose-button {/*++*/
	background-color: #fff;
	border-color: #fff;
	opacity: .5;
}
.l-header > .compose-button:hover {/*++*/
	opacity: 1;
}


.controls_section li:hover {/*++*/
	background-color: rgba(0, 0, 0, .5) !important;
}















































.left_column {background: none !important;}





.right_column {background: none !important;}



#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #fff !important; 
}

.controls_section_item > .posts > .hide_overflow {width:0}
.controls_section_item > .posts > .hide_overflow:before {
  content: "Posts";
  position: absolute;
}
.controls_section_item > .followers > .hide_overflow {width:0}
.controls_section_item > .followers > .hide_overflow:before {
  content: "Followers";
  position: absolute;
}
.controls_section_item > .activity > .hide_overflow {width:0}
.controls_section_item > .activity > .hide_overflow:before {
  content: "Activity";
  position: absolute;
}
.controls_section_item > .drafts > .hide_overflow {width:0}
.controls_section_item > .drafts > .hide_overflow:before {
  content: "Drafts";
  position: absolute;
}
.controls_section_item > .processing > .hide_overflow {width:0}
.controls_section_item > .processing > .hide_overflow:before {
  content: "Processing";
  position: absolute;
}
.controls_section_item > .queue > .hide_overflow {width:0}
.controls_section_item > .queue > .hide_overflow:before {
  content: "Queue";
  position: absolute;
}
.controls_section_item > .customize > .hide_overflow {width:0}
.controls_section_item > .customize > .hide_overflow:before {
  content: "Customize";
  position: absolute;
}






#right_column:after { 
display: none !important; 
}


.new_post_label/*_icon*/ > I {
display: none !important
}
  
  
.new_post_label { 
  padding-top: 70px !important;
  margin-left: -5px !important;
  color: rgba(0,0,0,0.5) !important;
}
    
    
.new_post_label:hover { 
      color: rgba(204,204,221,0.9) !important; 
}
        
        
#new_post {
background: #fff url(http://static.tumblr.com/kuehu0n/rZEmrk9ki/ziam_icons_bw.jpg) no-repeat center top; 
      padding: 10px !important; 
}
        
        
#new_post:hover {
background: #fff url(http://static.tumblr.com/kuehu0n/P1lmrk9qc/ziam_icons_col.jpg) no-repeat center top; 
      padding: -20px;
}
.l-content {
  background: transparent !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}




.mceContentBody {
  background: none !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes,
.selected .activity  {
  color: rgba(255,255,255, 0.50) !important; 
  background: transparent !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}

}
