css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#main-window:not([privatebrowsingmode])
#appmenu-button {
 background: rgb(76,76,76); /* Old browsers */
background: -moz-linear-gradient(top, rgba(76,76,76,1) 0%, rgba(89,89,89,1) 12%, rgba(102,102,102,1) 25%, rgba(71,71,71,1) 39%, rgba(44,44,44,1) 50%, rgba(0,0,0,1) 51%, rgba(17,17,17,1) 60%, rgba(43,43,43,1) 76%, rgba(28,28,28,1) 91%, rgba(19,19,19,1) 100%)!important;
  padding: 0px 10px 1px 12px !important;
  min-width: 0 !important;}
#appmenu-button .button-box .box-inherit .button-text { display: none;}
#appmenu-button dropmarker:before { content: "M4ksta \a0" !important; }

/* SUBMENUS */
popup:not(#autoscroller),
menupopup,
popup menu,
menupopup menu {
opacity: 0.95 !important;
-moz-appearance: none !important;
background: none !important;
border: none !important;
color: #fff !important;
box-shadow: none !important;
-moz-window-shadow: none !important;
}
popup scrollbox,
menupopup scrollbox {
margin: 10px 10px 14px !important;
}
/* MENU ITEMS */
menuitem,
.splitmenu-menuitem {
opacity: 0.95 !important;
-moz-appearance: none !important;
border: none !important;
color: #fff !important;
}
/* APP MENU */
#appmenu-popup vbox {
background: none !important;
border: none !important;
box-shadow: none !important;
}
/* MENUS */
popup:not(#autoscroller),
menupopup { border-radius:6px!important;
    border: 1px solid !important;
    border-color:#222!important;
   background: rgb(76,76,76)!important; 
  background: -moz-linear-gradient(-45deg, rgba(76,76,76,1) 0%, rgba(89,89,89,1) 12%, rgba(102,102,102,1) 25%, rgba(71,71,71,1) 39%, rgba(44,44,44,1) 50%, rgba(0,0,0,1) 51%, rgba(17,17,17,1) 60%, rgba(43,43,43,1) 76%, rgba(28,28,28,1) 91%, rgba(19,19,19,1) 100%)!important;}

menupopup menupopup {
margin-top: -13px !important;
}

/* SEPARATOR */
menuseparator {
-moz-appearance: none !important;
list-style-image: none !important;
background: none !important;
border: none !important;
border-top: solid .5px #555 !important;
}
/* DISABLED ITEM */
menuitem[disabled="true"] {
background: none !important;
opacity: 0.25 !important;
}
/* SELECTED ITEM */
popup menu:not([disabled="true"])[_moz-menuactive="true"],
menupopup menu:not([disabled="true"])[_moz-menuactive="true"],
menuitem:not([disabled="true"])[_moz-menuactive="true"],
.splitmenu-menuitem:not([disabled="true"])[_moz-menuactive="true"] {
background: -moz-linear-gradient(left, rgba(0,0,0,0.65) 0%, rgba(0,0,0,0) 100%)!important;
border-radius: 0px !important;
}
/* SELECTED ITEM (GRAPHITE) */
popup menu:-moz-system-metric(mac-graphite-theme)[_moz-menuactive="true"],
menupopup menu:-moz-system-metric(mac-graphite-theme)[_moz-menuactive="true"],
menuitem:not([disabled="true"]):-moz-system-metric(mac-graphite-theme)[_moz-menuactive="true"] {
background: -moz-linear-gradient(right, rgba(0,0,0,0.65) 0%, rgba(0,0,0,0) 100%)!important;}

/* RIGHT ARROW */
.menu-right,
autorepeatbutton {
-moz-appearance: none !important;
opacity: 0.4 !important;
list-style-image: url("data:image/png;base64,")
!important;
-moz-image-region: rect(0px 7px 9px 0px) !important;
margin-top: 1px !important;
}
.menu-right[_moz-menuactive="true"] {
opacity: 1.0 !important;
}
/* SCROLL ARROWS */
.autorepeatbutton-up,
.autorepeatbutton-down {
position: relative !important;
z-index: 1 !important;
opacity: 0.75 !important;
background: #333 !important;
}
.autorepeatbutton-up[disabled="true"],
.autorepeatbutton-down[disabled="true"] {
opacity: 0.4 !important;
}
/* CHECKMARK */
menuitem[checked="true"] .menu-iconic-left {
-moz-appearance: none !important;
opacity: 0.5 !important;
height: 16px !important;
list-style-image: none !important;
background: url("data:image/png;base64,")
-2px -1px no-repeat !important;
}
menuitem[checked="true"][_moz-menuactive="true"] .menu-iconic-left {
opacity: 1.0 !important;
}
