css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Toolbar */
#back-button { list-style-image: url("moz-icon://stock/go-previous?size=toolbar") !important; }
#back-button[disabled="true"] { list-style-image: url("moz-icon://stock/go-previous?size=toolbar&state=disabled") !important; }

#forward-button { list-style-image: url("moz-icon://stock/go-next?size=toolbar") !important; }
#forward-button[disabled="true"] {list-style-image: url("moz-icon://stock/go-next?size=toolbar&state=disabled") !important;}

#home-button { list-style-image: url("moz-icon://stock/go-home?size=toolbar") !important; }
#home-button[disabled="true"] { list-style-image: url("moz-icon://stock/go-home?size=toolbar&state=disabled") !important; }

#reload-button { list-style-image: url("moz-icon://stock/view-refresh?size=toolbar") !important; }
#reload-button[disabled="true"] { list-style-image: url("moz-icon://stock/view-refresh?size=toolbar&state=disabled") !important; }

#stop-button { list-style-image: url("moz-icon://stock/process-stop?size=toolbar") !important; }
#stop-button[disabled="true"] { list-style-image: url("moz-icon://stock/process-stop?size=toolbar&state=disabled") !important; }

/* Toolbar (small icons) */
toolbar[iconsize="small"] #back-button { list-style-image: url("moz-icon://stock/go-previous?size=menu") !important; }
toolbar[iconsize="small"] #back-button[disabled="true"] { list-style-image: url("moz-icon://stock/go-previous?size=menu&state=disabled") !important; }

toolbar[iconsize="small"] #forward-button { list-style-image: url("moz-icon://stock/go-next?size=menu") !important; }
toolbar[iconsize="small"] #forward-button[disabled="true"] { list-style-image: url("moz-icon://stock/go-next?size=menu&state=disabled") !important; }

toolbar[iconsize="small"] #home-button { list-style-image: url("moz-icon://stock/go-home?size=menu") !important; }
toolbar[iconsize="small"] #home-button[disabled="true"] { list-style-image: url("moz-icon://stock/go-home?size=menu&state=disabled") !important; }

toolbar[iconsize="small"] #reload-button { list-style-image: url("moz-icon://stock/view-refresh?size=menu") !important; }
toolbar[iconsize="small"] #reload-button[disabled="true"] { list-style-image: url("moz-icon://stock/view-refresh?size=menu&state=disabled") !important; }

toolbar[iconsize="small"] #stop-button { list-style-image: url("moz-icon://stock/process-stop?size=menu") !important; }
toolbar[iconsize="small"] #stop-button[disabled="true"] { list-style-image: url("moz-icon://stock/process-stop?size=menu&state=disabled") !important; }

/* URLbar */
#urlbar-reload-button { list-style-image: url("moz-icon://stock/view-refresh?size=menu") !important; }
#urlbar-stop-button { list-style-image: url("moz-icon://stock/process-stop?size=menu") !important; }

/* Tabbar */
.tab-close-button { list-style-image: url("moz-icon://stock/window-close?size=menu") !important; }
.tabs-newtab-button { list-style-image: url("moz-icon://stock/tab-new?size=menu") !important; }

/* Addons Manager */
.addon-control.preferences { list-style-image: url("moz-icon://stock/preferences-other?size=menu") !important; }

.addon-control.disable, .addon-control.remove { list-style-image: url("moz-icon://stock/list-remove?size=button") !important; }
.addon-control.disable[disabled="true"], .addon-control.remove[disabled="true"]  { list-style-image: url("moz-icon://stock/list-remove?size=button&state=disabled") !important; }

.addon-control.enable { list-style-image: url("moz-icon://stock/dialog-ok?size=button") !important; }
.addon-control.enable[disabled="true"] { list-style-image: url("moz-icon://stock/dialog-ok?size=button&state=disabled") !important; }

/* Textbox Icons */
.textbox-search-icon { list-style-image: url("moz-icon://stock/edit-find?size=menu") !important; }
.textbox-search-clear { list-style-image: url("moz-icon://stock/edit-clear?size=menu") !important; }

/* Menu Items */
menuitem[cmd="cmd_undo"] { list-style-image: url("moz-icon://stock/edit-undo?size=menu") !important; }
menuitem[cmd="cmd_undo"][disabled] { list-style-image: url("moz-icon://stock/edit-undo?size=menu&state=disabled") !important; }

menuitem[cmd="cmd_cut"] { list-style-image: url("moz-icon://stock/edit-cut?size=menu") !important; }
menuitem[cmd="cmd_cut"][disabled] { list-style-image: url("moz-icon://stock/edit-cut?size=menu&state=disabled") !important; }

menuitem[cmd="cmd_copy"] { list-style-image: url("moz-icon://stock/edit-copy?size=menu") !important; }
menuitem[cmd="cmd_copy"][disabled] { list-style-image: url("moz-icon://stock/edit-copy?size=menu&state=disabled") !important; }

menuitem[cmd="cmd_paste"] { list-style-image: url("moz-icon://stock/edit-paste?size=menu") !important; }
menuitem[cmd="cmd_paste"][disabled] { list-style-image: url("moz-icon://stock/edit-paste?size=menu&state=disabled") !important; }

menuitem[cmd="cmd_delete"] { list-style-image: url("moz-icon://stock/edit-delete?size=menu") !important; }
menuitem[cmd="cmd_delete"][disabled] { list-style-image: url("moz-icon://stock/edit-delete?size=menu&state=disabled") !important; }

menuitem[cmd="cmd_selectAll"] { list-style-image: url("moz-icon://stock/edit-select-all?size=menu") !important; }
menuitem[cmd="cmd_selectAll"][disabled] { list-style-image: url("moz-icon://stock/edit-select-all?size=menu&state=disabled") !important; }

/* Dialog Buttons */
button[icon="accept"] .button-icon { list-style-image: url("moz-icon://stock/dialog-ok?size=button") !important; }
button[icon="accept"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/dialog-ok?size=button&state=disabled") !important; }

button[icon="cancel"] .button-icon { list-style-image: url("moz-icon://stock/dialog-cancel?size=button") !important; }
button[icon="cancel"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/dialog-cancel?size=button&state=disabled") !important; }

button[icon="help"] .button-icon { list-style-image: url("moz-icon://stock/help-contents?size=button") !important; }
button[icon="help"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/help-contents?size=button&state=disabled") !important; }

button[icon="open"] .button-icon { list-style-image: url("moz-icon://stock/document-open?size=button") !important; }
button[icon="open"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/document-open?size=button&state=disabled") !important; }

button[icon="save"] .button-icon { list-style-image: url("moz-icon://stock/document-save?size=button") !important; }
button[icon="save"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/document-save?size=button&state=disabled") !important; }

button[icon="find"] .button-icon { list-style-image: url("moz-icon://stock/edit-find?size=button") !important; }
button[icon="find"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/edit-find?size=button&state=disabled") !important; }

button[icon="clear"] .button-icon { list-style-image: url("moz-icon://stock/edit-clear?size=button") !important; }
button[icon="clear"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/edit-clear?size=button&state=disabled") !important; }

button[icon="yes"] .button-icon { list-style-image: url("moz-icon://stock/dialog-ok?size=button") !important; }
button[icon="yes"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/dialog-ok?size=button&state=disabled") !important; }

button[icon="no"] .button-icon { list-style-image: url("moz-icon://stock/dialog-no?size=button") !important; }
button[icon="no"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/dialog-no?size=button&state=disabled") !important; }

button[icon="apply"] .button-icon { list-style-image: url("moz-icon://stock/dialog-apply?size=button") !important; }
button[icon="apply"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/dialog-apply?size=button&state=disabled") !important; }

button[icon="close"] .button-icon { list-style-image: url("moz-icon://stock/dialog-close?size=button") !important; }
button[icon="close"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/dialog-close?size=button&state=disabled") !important; }

button[icon="print"] .button-icon { list-style-image: url("moz-icon://stock/document-print?size=button") !important; }
button[icon="print"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/document-print?size=button&state=disabled") !important; }

button[icon="add"] .button-icon { list-style-image: url("moz-icon://stock/list-add?size=button") !important; }
button[icon="add"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/list-add?size=button&state=disabled") !important; }

button[icon="remove"] .button-icon { list-style-image: url("moz-icon://stock/list-remove?size=button") !important; }
button[icon="remove"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/list-remove?size=button&state=disabled") !important; }

button[icon="refresh"] .button-icon { list-style-image: url("moz-icon://stock/view-refresh?size=button") !important; }
button[icon="refresh"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/view-refresh?size=button&state=disabled") !important; }

button[icon="revert"] .button-icon { list-style-image: url("moz-icon://stock/document-revert?size=button") !important; }
button[icon="revert"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/document-revert?size=button&state=disabled") !important; }

button[icon="go-forward"] .button-icon { list-style-image: url("moz-icon://stock/go-next?size=button") !important; }
button[icon="go-forward"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/go-next?size=button&state=disabled") !important; }

button[icon="go-forward"]:-moz-locale-dir(rtl) .button-icon { list-style-image: url("moz-icon://stock/go-previous?size=button") !important; }
button[icon="go-forward"]:-moz-locale-dir(rtl)[disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/go-previous?size=button&state=disabled") !important; }

button[icon="go-back"] .button-icon { list-style-image: url("moz-icon://stock/go-previous?size=button") !important; }
button[icon="go-back"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/go-previous?size=button&state=disabled") !important; }

button[icon="go-back"]:-moz-locale-dir(rtl) .button-icon { list-style-image: url("moz-icon://stock/go-next?size=button") !important; }
button[icon="go-back"]:-moz-locale-dir(rtl)[disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/go-next?size=button&state=disabled") !important; }

button[icon="properties"] .button-icon { list-style-image: url("moz-icon://stock/document-properties?size=button") !important; }
button[icon="properties"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/document-properties?size=button&state=disabled") !important; }

button[icon="select-font"] .button-icon { list-style-image: url("moz-icon://stock/draw-text?size=button") !important; }
button[icon="select-font"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/draw-text?size=button&state=disabled") !important; }

/* Commented out due to inconsistencies between icon themes
   button[icon="select-color"] .button-icon { list-style-image: url("moz-icon://stock/color-picker?size=button") !important; }
   button[icon="select-color"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/color-picker?size=button&state=disabled") !important; }
 */

/* Kinda hackish so as to support elementary too. Replace folder-remote with folder-network for actual proper image. */
button[icon="network"] .button-icon { list-style-image: url("moz-icon://stock/folder-remote?size=button") !important; }
button[icon="network"][disabled="true"] .button-icon { list-style-image: url("moz-icon://stock/folder-remote?size=button&state=disabled") !important; }
