css:
@-moz-document domain("mail.google.com") {
/*Set gmail tab colors*/

/*Extends height of color bar above tab to full height of tab, and moves it behind text and icon*/
.aKo
{
    height:45px !important; 
    position:absolute !important; 
    z-index:0 !important;
  	top:0 !important;
}


/*Tab colors - Replace with your own values.*/

/*Primary*/
.aIf-aLe .aDG, .aAy.J-KU-JW.aAy.aIf-aLe .aKo, .aAy.J-KU-KO.aAy.aIf-aLe .aKo {
    background-color: #eb9bff !important;
}

/*Social*/
.aKe-aLe .aDG, .aAy.J-KU-JW.aAy.aKe-aLe .aKo, .aAy.J-KU-KO.aAy.aKe-aLe .aKo {
    background-color: #4986E7 !important;
}

/*Promotions*/
.aJi-aLe .aDG, .aAy.J-KU-JW.aAy.aJi-aLe .aKo, .aAy.J-KU-KO.aAy.aJi-aLe .aKo {
    background-color: #16A765 !important;
}

/*Updates*/
.aH2-aLe .aDG, .aAy.J-KU-JW.aAy.aH2-aLe .aKo, .aAy.J-KU-KO.aAy.aH2-aLe .aKo {
    background-color: #E9B330 !important;
}

/*Forums*/
.aHE-aLe .aDG, .aAy.J-KU-JW.aAy.aHE-aLe .aKo, .aAy.J-KU-KO.aAy.aHE-aLe .aKo {
    background-color: #A479E2 !important;
}

/*Optional transparent gradient overlay for all tabs. Commas are essential at end of all lines except the last */

.aIf-aLe .aDG, .aAy.J-KU-JW.aAy.aIf-aLe .aKo, .aAy.J-KU-KO.aAy.aIf-aLe .aKo,
.aKe-aLe .aDG, .aAy.J-KU-JW.aAy.aKe-aLe .aKo, .aAy.J-KU-KO.aAy.aKe-aLe .aKo,
.aJi-aLe .aDG, .aAy.J-KU-JW.aAy.aJi-aLe .aKo, .aAy.J-KU-KO.aAy.aJi-aLe .aKo,
.aH2-aLe .aDG, .aAy.J-KU-JW.aAy.aH2-aLe .aKo, .aAy.J-KU-KO.aAy.aH2-aLe .aKo,
.aHE-aLe .aDG, .aAy.J-KU-JW.aAy.aHE-aLe .aKo, .aAy.J-KU-KO.aAy.aHE-aLe .aKo
{
background: -moz-linear-gradient(top, rgba(255,255,255,.5) 0%, rgba(255,255,255,0) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,255,255,.5)), color-stop(100%,rgba(255,255,255,0)));
background: -webkit-linear-gradient(top, rgba(255,255,255,.5) 0%,rgba(255,255,255,0) 100%);
background: -o-linear-gradient(top, rgba(255,255,255,.5) 0%,rgba(255,255,255,0) 100%);
background: -ms-linear-gradient(top, rgba(255,255,255,.5) 0%,rgba(255,255,255,0) 100%);
background: linear-gradient(to bottom, rgba(255,255,255,.5) 0%,rgba(255,255,255,0) 100%);
}


/*BEGIN: Optional rounded corners. Rounds top left and right corners of tabs so they really look like tabs. You can change the value of the corners to more or less than 10px */

.aAy, .aKo, .aKj {
	-moz-border-radius: 10px 10px 0 0;
	-webkit-border-radius: 10px 10px 0 0;
  	border-radius: 10px 10px 0 0;
    
}
/*Adds border to rounded tabs*/
    .aAy, .aKj {
    border:1px solid #ccc;
    }
  
.aKu {display:none !important;
}

.aKj {
	border: 1px solid #ccc;
	border-left-width: 1px !important;
}
/*END: Optional rounded corners*/


}
