css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("b.hatena.ne.jp") {

#header, #page-title, #profile-container, #userpage_invite, #userpage_usersuggest, #feedback-pane,
#right-container, .main-entry-list blockquote, .header .profile-image, .timestamp {
    display: none !important;
}

#bookmark-page #user-search { margin: 0 !important; }

#center-container { width: 740px !important; }

.entry-block {
    border-width: 0 !important;
    padding: 15px 9px 9px !important;
}

.entry-block:nth-of-type(even) {
    background-color: #EFF3F7;
}

.entry-comment { display: inline !important; }

.entry-comment li {
    padding: 6px 0 !important;
    border-width: 0 !important;
}

html * { font-size: 100% !important; }

.header { display: inline !important; }

.user-tag:before { content: "\00005b"; }
.user-tag:after { content: "\00005d"; }

.users a {
    color: #FF9191 !important;
    font-weight: normal !important;
    text-decoration: none !important;
}

}
