css:
@-moz-document domain("trello.com") {
html {
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
}

body {
  font-family: 'Gotham' !important;
}

#header {
  background-image: linear-gradient( #5799dd,#266fbc);
  border-bottom: 1px solid #143c66;
  box-shadow: 0 2px 3px rgba(0,0,0,.25);
  text-shadow: 0 1px 0 #143c66;
}

.header-btn-text {
  line-height: 32px;
}

.board-canvas {
  background: #373A46 !important;
  padding-top: 12px;
}
  
.list {
  background: #EEEEEE;
}
    
.list-header {
  margin: 5px 0;
}
}
