css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

body { background: url('http://media.tumblr.com/tumblr_maxkkwDlOC1qi3r3q.png') center right #000000 repeat fixed !important; }

#tagged_display div.l-content, #dashboard_messages_inbox div.l-content, #clearfix div.l-content, #dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0.2) !important;}

#dashboard_edit_post{background-color: #000000 !important}

#right_column .dashboard_nav_item {border-bottom:0px solid #eee !important;background-image:none  !important}
#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {color:#FFF !important;}
#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {color:#888 !important ;}#right_column .dashboard_nav_item 
#dashboard_controls_radar_media_fold {background: none !important;}
#left_column #posts .notification .avatar {border-color:transparent !important;background-color:#000000 !important;opacity:0.5 !important;}
#left_column #posts .notification .avatar:hover{opacity:100 !important;}.dashboard_switch_blog_menu_header{background-color:#000000 !important;color:#000 !important;}.dashboard_switch_blog_menu_item

{background-color:#FFFFFF !important;color:#000 !important;border:0px !important}.dashboard_switch_blog_menu_footer{background-color:#ffffff !important;color:#000 !important;}.dashboard_switch_blog_menu_wrapper{background-color:#000000 !important;}#new_post_notice_container{color:#fff!important}#new_post_notice_container a


.new_post_label{
font-size:0px !important;
}

.new_post_label_icon{background-image:none !important}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
#new_post{
background: url(http://media.tumblr.com/tumblr_ma5vlg7T331qi3r3q.png) center no-repeat !important;
background-color:#fff !important;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}

#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://media.tumblr.com/tumblr_ma5x5cbwe71qi3r3q.png') !important;

}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://media.tumblr.com/tumblr_ma5zbnRJHG1qi3r3q.png")!important;background-repeat:no-repeat!important;width:23px!important;height:77px!important;margin-top:-12px!important}.selection_nipple.white{background-position:0 0!important;width:35px!important;height:35px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}#inbox_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-10px!important}


#return_to_top .return_to_top_icon{ background: url('http://media.tumblr.com/tumblr_ma61x6EmlH1qi3r3q.png') no-repeat transparent!important}


#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 207px !important;
  height: 80px !important;
  background-repeat: no-repeat;
  background: url("http://media.tumblr.com/tumblr_ma64fnc1kv1qi3r3q.png")!important;
}}
