@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https\:\/\/myspace\.com\/(.+)$"),
  regexp("^https\:\/\/myspace\.com\/(.+)\/(.+)$"), 
  url("https://myspace.com/")
{

    /*Text Stuff*/
    
    #hCard > ul,
    #links,
    .dotted,
    .flex,
    a:link,
    body,
    button,
    div,
    div > time,
    input,
    label,
    nav,
    p,
    span,
    textarea{
    color:#141414 !important; text-shadow: 0px 0px 1px #000000 !important;
    }
       
    h2,
    h4,
    h5,
    footer,
    .dotted{
    color: #141414 !important; background-color: inherit !important;
    }
    
    #desktop-template #homepage #featured-slider .text a, 
    #desktop-template #featured-slider .item .category,
    .info > .description, 
    .info > .artist, 
    #page_header > h2 > a,
    #cover-attribution, 
    #byline_date, 
    #byline_date > span{
    color: #f1f1f1 !important; background-color: inherit !important; text-shadow: 1px 0px 1px #000000 !important;
    }
    
    #sign-in{
    display:inherit !important;
    }
    
    /*End Text Stuff*/
    /*Alignment Stuff*/
    
    #sidebar > nav ul,
    #sidebar > nav,
    #player,
    #sidebar > nav ul > li > a {
    border-bottom: 1px solid #f1f1f1 !important;
    border-top: 1px solid #f1f1f1 !important;
    }

    #global, 
    #lightboxCover{
    bottom:0px !important;
    }
    
    #drawer{
    padding-bottom:57px !important;
    }
    
    #background,
    .horizontalContent{
    bottom:-20px !important;
    }
    
    .chat.popout{
    bottom:15px !important; 
    left:-17px !important;
    }
    
    #footer{
    left:20px !important;
    }
    
    #notificationsPopout, 
    #conversationsPopout{
    bottom:56px !important;
    }

    #articleWrap > .articleHeader{
    right:10px !important;
    margin-left:20px !important;
    }
    
    #global-nav > nav, 
    #player{
    margin-right:10px !important;
    }

      
    #modeControls button.radio_20{
    background-position: 0px 0 !important; 
    }
    
    .privacy_terms > header{
    position:fixed !important; left:150px !important;
    }
    
    /*End Alignment Stuff*/
    /*Background/Theme Stuff*/
       
    #drawer,
    #drawer.message,
    #message,
    #messaging .list.options > li,
    #mixes .list li.util,
    #profileWidget,
    #radio .options li,
    #radioSearch,
    #sidebar > nav > ul > .hasSubNav > ul > li.selected a,
    #sidebar > nav > ul > .hasSubNav > ul > li:hover,
    #sidebar > nav ul > li > a:hover,
    #videoPlayer > div > .dynamicUI > .liveStreamDrawer,
    .connectButton:hover,
    .edit.noStyle,
    .fbShare,
    .gradient:after,
    .isotope .gradient:after,
    .likeBtn:hover,
    .liveLabel,
    .massive,
    .options > button:hover,
    .playBtn.play_24:hover,
    .playBtn.play_25:hover,
    .playBtn.play_30:hover,
    .playBtn.play_50:hover,
    .playBtn.play_55:hover,
    .portfolioItem > div > div,
    .POVs > ul > li,
    .rank,
    .songTitle,
    .tip-inner,
    .twitterShare,
    ul > .selected, 
    #article-category-nav ul a.selected,
    #streamFilterBtn > button:hover{
    background: rgba(184, 184, 184, 0.5) !important;
    }
    
    #foreground,
    #global-nav .meter,
    #hCard > ul > .hovered,
    #hCard > ul > li:hover,
    #medialist > ol > .heading,
    #modeControls > button,
    #profileImageUpload > strong,
    #sidebar > div,
    #sidebar > footer > #btnMoreInfo,
    #sidebar > header,
    #sidebar > nav,
    #sidebar > nav > ul > .hasSubNav > ul,
    #sidebar > nav > ul > li.selected,
    #site-info-popup,
    #streamNav > div > button,
    #userid209419,
    #videoPlayer.live > div > div > footer,
    #volumeControl > button,
    .artistInfoTray,
    .connectButton,
    .edit.noStyle:hover,
    .fbShare:hover,
    .flex,
    .formFooter > .primary,
    .likeBtn,
    .messages,
    .playBtn.play_30,
    .twitterShare:hover,
    aside,
    dl,
    form,
    header,
    input,
    textarea,
     #previous_article, 
    #people-browse-widget, 
    .people-search > form, 
    .beam-articles-profiling #featured, 
    #featured-article,
    a > .title, 
    #filters-form, 
    .song, 
    #wrap > #songs, 
    #wrap > #video, 
    #wrap > #people, 
    #wrap > #homepage, 
    .prev-series, 
    .next-series{
    background-color: #f1f1f1 !important;
    }
    
    #foreground > #radioTray > div,
    #global,
    #hCard > ul,
    #inlineRightRail > .commentsControl,
    #inlineRightRail > section > header,
    #lightboxCover,
    #mashupModule,
    #messageBar > div > section > .messages,
    #modeControls > button:hover,
    #sidebar > nav > ul > .hasSubNav > ul > li.selected,
    #sidebar > nav > ul > .hasSubNav > ul > li:hover,
    #sidebar > nav > ul > li:hover,
    #streamFilterTray,
    #unifiedSidebar > div,
    #unifiedSidebar,
    .button,
    button,
    #volumeControl > button:hover,
    .beam-home,
    .formFooter,
    .homeSearchTray,
    .lightbox > header,
    .myspace-logo.svg,
    .notification,
    .popout,
    .primary,
    .rr > .stats,
    .sign-up-sign-in,
    aside,
    form, 
    #global-nav, 
    #articleContent, 
    .article_related > .header{
    background-color: #ffffff !important; border:none !important;}
    
    
    #messaging .list.active > li.online.open,
    a:after, 
    .trailer{
    background-color:#377778 !important; background-image:linear-gradient(#5C5C5C, #4D4D4D) !important;
    }
    
    #global-nav .meter > .buffer{
    background-color:#FF6F21 !important; opacity: 0.3 !important; background-image:linear-gradient(#FF5900, #FF6F21) !important;
    }
    
    #superPost, 
    #streamFilterBtn > button, 
    #sidebar > footer > #btnMoreInfo{
    box-shadow:0 0 7px 0 rgba(50, 50, 50, 0.45) !important;
    }
    
    #container > #wrap > section > div > aside,
    #container > #wrap > section > div > header,
    #contentWrap > .aboutMePanel > aside,
    #notificationsForm,
    #privacyForm,
    .settingsAccordion,
    .settingsAccordion > form,
    .topFansIntro > header{
    background-color:#e5e5e5 !important;
    }
      
    #global-nav > nav > ul > li.selected > a, 
    #global-nav .meter{
    border-bottom-color: #f1f1f1 !important;
    }
    
    #player,
    #global-nav > nav > ul:first-child{
    background-color:#bbbbbb !important;
    }
    
    #profileImageUpload > ul > li:hover > button,
    .primary:hover, 
    #global-nav > nav > ul{
    background-image:linear-gradient(#d3d3d3, #d3d3d3) !important;
    }
    
    #settings-link > span > i, 
    #showNotificationsNavLink > span > i, 
    #messagingLink > span > i{
    background-color: #d3d3d3 !important;
    }
    
    #profileImageUpload > ul > li > button,
    #drawer .list .selected,
    #drawer .list li:hover, 
    #confirmDialog,  
    #playControls > button{
    background-image:linear-gradient(#f1f1f1, #f1f1f1) !important;
    }
            
    #playControls > button:hover{
    background-image:linear-gradient(#ffffff, #ffffff) !important;
    }
    
    .playBtn.play_25.playing, 
    .playBtn.play_25.playing:hover{
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAYAQMAAADaua+7AAAABlBMVEUAAADU1NRLDTL1AAAAAXRSTlMAQObYZgAAABBJREFUCFtjYMAHnhOJcQMAsY8JB2RO4FgAAAAASUVORK5CYII=") !important;  
    }
    
    .left > .song, 
    .controls > .song,
    .paused, 
    .playBtn.play_25.mixtape, 
    .playBtn.play_25.album, 
    .playBtn.play_30.radio,
    .playBtn.play_24, 
    .playBtn.play_25, 
    .playBtn.play_30, 
    .playBtn.play_50, 
    .playBtn.play_55{
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAYAAADgdz34AAAAdElEQVR42u3UsQmAMBBG4YDgEoIg2LqHkziAYOs2VrYO4ACW6VxCENKer0uT0l8QUnz1g7vjnJlJ5UAOfBTw3i+o4BJeCRguDLpAtKPVBKKACYUmEB3olIEboyqwolaM6ESvWHLAjFJxphsauIT8i3Lg74EHQE65L23GNO8AAAAASUVORK5CYII=") !important;
      background-position:center !important;  
    }
    
    .playBtn.play_24:hover, 
    .playBtn.play_25:hover, 
    .playBtn.play_30:hover, 
    .playBtn.play_50:hover, 
    .playBtn.play_55:hover{
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABkAAAAZCAYAAADE6YVjAAAAVklEQVR42u3UQQ0AIAwDQCRMElKQMilImSQcjCU8qqAhhDbp+zJgtMykV4iQu4hXjYucrOrgIkhUOxdBZtW4CI7QuQgSr06CO3ntdWFP6BuvD1LIx8gGCwF8sTbuikUAAAAASUVORK5CYII=") !important;
      background-position:center !important;  
    }
    
    .shuffle_20:hover{
    background-image: url("data:image/png;base64,") !important;
    }
    
    .repeat_20:hover{
    background-image: url("data:image/png;base64,") !important;
    }
    
    .volume_off:hover{
    background-image: url("data:image/png;base64,")
    }
    
    .volume_low:hover{
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABQAAAAUCAYAAACNiR0NAAAAUklEQVR42u3UoQoAIAyEYcFXN1lNwsD38OXOtZksO1DR8NevbFwAQO0iUERWJSaYNLDArIEFFg0esBtgeUB8EMcdZS5qjQdalQzag28bhwcHdgDm12lOE33WbwAAAABJRU5ErkJggg==")
    }
    
    .volume_mid:hover{
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABQAAAAUCAYAAACNiR0NAAAAiElEQVR42uXUwQmAMAwFUEFwGK9uUHAAr27gHp689iQU3MNJhA4TKwQDJWhNc1A8fGh7eBCapAAA1XwIdM5dZWTeBCBhoAVOBxaBRgraEyPQ4LlPAdcIAAasQnzIlgLCHYgZ8F5rgQ3eu7eCVLLmp/inbVOGLLltw2VmSm5zR88KRk9vOfxwwe5u5SV0gHoKMwAAAABJRU5ErkJggg==") !important;
    }
    
    .volume_max:hover{
    background-image: url("data:image/png;base64,")
    }
    
    .myspace-logo.svg{
    background-image: url("https://a2-images.myspacecdn.com/images04/10/288016c0fc8140b18d161844e2769ff8/full.jpg")
    }
    
    #featuredArtistTray{
    background-image:none !important;
    }

    .portfolioItem > .media > div > h4, 
    .portfolioItem > .media > .hideGradient:after,
    .portfolioItem > .media > a:after, 
    .radio-input, 
    .articleHeader > #page_header, 
    #searchContainer{
    background:none !important;
    }
    
    #people-browse-widget{
    background-image:none !important;
    }
       
    .messageGroup .sender.online.presence:before,
    .presence.online > a:before,
    a.presence.online:before{
    background-color:#01bc72 !important;
    }
    
    #videoPlayer > div > .close{
    background-color:transparent !important;
    }
    
    h1, 
    .settingsAccordion {
    border-radius: 2px 2px 2px 2px !important;
    }
    
     /*End Background/Theme Stuff*/
	 
     /*Hidden Stuff*/

    #featuredArtistTray,
    #globalSuperPost,
    #links,
    #myspaceTip,
    #search,
    .container-main > ul > li:nth-child(3),
    .standaloneWTPlayer,
    footer > div > nav > ul > li:nth-child(-n+1){
    display:none !important;
    }
    
    /*End Hidden Stuff*/
    
}
