css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
background: url('http://i.imgur.com/weQ8Vea.jpg') center center #ed6100 no-repeat fixed !important;
}

div.l-header .selection_nipple {
border-bottom: 12px solid rgba(60,25,0,0.4) !important;
}

.mceContentBody {
background: white !important;
}
  
.chrome.blue {
border-color: #3c1900 !important;
background: -webkit-linear-gradient(top,#decfb6 0,#5d3d24 100%) !important;
}

#post_controls button.close {
background-color: #ae987f !important;
border-color: transparent !important
}
 
ol#posts li.notification.single_notification {
background-color: rgba(0,0,0,0.4) !important;
}

.post_header {
color: #773b00 !important;
}

.post_footer {
background: transparent !important;
}
  
div.l-content .post_info a {
color: rgba(119,59,0,.35) !important;
}
  
.post_notes_label.note_count {
color: rgba(119,59,0,.35) !important;
}

.post_notes_label.note_count:hover {
color: rgba(119,59,0,.5) !important;
}
 
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent;
}
 
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(60,25,0,0.35) !important;
}

div.l-content #posts .post {
background-color: #fff3da !important;
}

.conversation_lines .chat_line:nth-child(even) {
background: #ffedc6 !important;

}

.conversation_lines {
background: #fff9ee !important;

}

.post_full.is_conversation .conversation_lines {
border: 1px solid rgba(119,59,0,.35) !important;
  
}

#posts .post_content blockquote {
border-left: solid 3px rgba(119,59,0,.35) !important;
padding-left: 8px;
margin-left: 6px;
    
}
      
li.chat_line {
border-bottom: 1px solid rgba(0,0,0,0) !important;

}

.post_source {
color: rgba(119,59,0,.35) !important;

}
  
.post_source a {
color: rgba(119,59,0,.35) !important;
}

#pagination a {
background-color: transparent !important;
color: #fff3da !important;
}

.post_tags a, .post .post_tags .post_tag {
color: rgba(119,59,0,.35) !important;
}
  
.link_button .link_title {
color: rgba(119,59,0,.6) !important;
}
      
.post_content {
background: rgba(0,0,0,0) !important;
}
 
 
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: transparent;
}

#dashboard_controls a, #dashboard_controls .control {
color: #cfb290 !important;
}

#dashboard_controls {
color: #cfb290 !important;
}
  
#nav .nav_item.active {
background: transparent !important;
}
 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: transparent !important;
}

.controls_section.user_list li a:before {
background: transparent !important;
border-right: 5px transparent !important;
}

div.ui_follow:hover, .controls_section li .follow:hover {
background: rgba(60,25,0,0.35) !important;
}

div.ui_follow, .controls_section li div.follow {
color: #fff3da !important;
line-height: 8px !important;
font-size: 24px !important;
}
 
.notification  {
background: rgba(0,0,0,0.2) !important;
border:none !important; 
font-size: 11px;
}
 
ol#posts li.post .permalink {
background: transparent;
}

#right_column{
background: rgba(0,0,0,0) !important;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.2) !important;
}
 
#right_column:after{
background: transparent !important;
}
 
#left_column #posts .post .permalink{
background: transparent !important;
}

.controls_section li:not(.no_push):not(.section_header):not(.popover_blogs).selected a, .controls_section li:not(.no_push):not(.section_header):not(.popover_blogs).selected:hover a, .controls_section li:not(.no_push):not(.section_header):not(.popover_blogs):active a, .controls_section li:not(.no_push):not(.section_header):not(.popover_blogs):active:hover a {
color: #fff3da !important;
}

.post_permalink {
background: url('http://static.tumblr.com/o7cvzyg/FhTmskop8/leaf4.gif') !important;
width: 23px !important;
height: 21px !important;
display: inline !important;
}

.post_permalink:hover {
background: url('http://static.tumblr.com/o7cvzyg/KfGmskopt/leaf3.gif') !important;
width: 23px !important;
height: 21px !important;
display: inline !important;
}

.post_full .post_permalink:before {
border-right: 16px transparent !important;
}

.post_full.is_note .asker, .post_full.is_note .answerer {
color: rgba(119,59,0,.35) !important;
}

.post_full.is_note .answerer .name, .post_full.is_note .asker .name {
color: rgba(119,59,0,.50) !important;
}

.post_full.is_note .post_body .note_item {
background-color: #efe1c8 !important;
border: 1px solid #decfb6 !important;
}

.xtimestamp {
color: rgba(119,59,0,.35) !important;
margin-bottom: 0px !important;
}

.plexi.color {
background: rgba(115,70,51,0.7) !important;
}

#glass_overlay.show {
display: none !important;
}

.search_form_row {
background-color: rgba(0,0,0,.10) !important;
}

#footer a {
color: rgba(255, 243, 218,.69) !important;
}
}
