css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("ficbook.net") {
TABLE.loggedin_bar{height:53px!important;width:970px!important}
table[align="right"]{width:970px!important;position:absolute;margin-left:-485px;left:50%}
TD.loggedin_cell{height:40px}
TD.home_menu{width:160px!important}
td[width="516"],TD.part_right{width:0}
DIV.author_list{width:auto!important}
DIV.author_list DIV{width:50%!important}
TD.dop_nav,TD.top_banner_cell{position:absolute;right:20px;top:30px}
td.dop_nav {margin-top:30px}
DIV.part_text{width:95%!important}
TABLE.comment_form_table{margin-top:40px!important}
DIV.submit_button INPUT{width:100px;padding:6px}
DIV.user_drop_down[style="position: absolute; left: -65px; z-index: 100; display: block;"]{left:400px!important;top:84px!important;float:none!important;width:200px!important;box-shadow:4px 4px 16px #444}
.ya-site-form__search-input-layout{width:200px;z-index:99999;float:right}
.user_drop_down{margin-left:-80px!important;left:inherit!important;margin-top:16px!important}

/*ШРИФТЫ*/
BODY{font-family:Tahoma!important}
small{font-size:100%}
A{text-decoration:none!important;color:#000!important}
A.white{color:#fff!important}
A:hover{text-shadow:0px 0px 6px rgba(255,255,255,0.7)!important}
A.white:hover{color:#fff!important}
A.user_home:hover{text-shadow:none!important}
.public_beta{white-space:normal}
TABLE{font-size:12px}
TD.dop_nav,TD.top_banner_cell{text-align:right}
DIV.part_text{font-size:14px!important}
DIV.submit_button INPUT{box-shadow:4px 4px 16px #444;color:#000}
A.like {color:#C50000!important}
DIV.user_drop_down[style="position: absolute; left: -65px; z-index: 100; display: block;"]{color:#fff!important;font-size:100%!important;text-align:center!important}

/*ГРАНИЦЫ*/
A.user_home{border-bottom:none!important}
DIV.submit_button INPUT,
A.like,
TEXTAREA,DIV.comment_thumb,
DIV.user_drop_down[style="position: absolute; left: -65px; z-index: 100; display: block;"] {
	border:none!important;
}
DIV.user_drop_down_separator{border:1px solid #fff!important}

/*ФОНЫ*/
tr[style="background-color: #ffff99"],
td[background="http://teinon-st.cdn.ngenix.net/design/menu/menu_02.png"],
td[background="https://teinon.net/ficbook/design/menu/menu_02.png"],
td[background="https://ficbook.net/design/menu/menu_02.png"],
td[background="http://teinon-st.cdn.ngenix.net/design/book/top/book_01.png"],
td[background="http://teinon-st.cdn.ngenix.net/design/book/left/book_02.png"],
td[background="https://teinon.net/ficbook/design/book/left/book_02.png"],
td[background="https://ficbook.net/design/book/left/book_02.png"],
td[background="http://teinon-st.cdn.ngenix.net/design/book/right/book_02.png"],
td[background="http://teinon-st.cdn.ngenix.net/design/book/right/book_02.png"],
td[background="https://teinon.net/ficbook/design/book/right/book_02.png"],
td[background="https://ficbook.net/design/book/right/book_02.png"],
td[background="http://teinon-st.cdn.ngenix.net/design/book/bottom/book_02.png"],
td[background="https://teinon.net/ficbook/design/book/bottom/book_02.png"],
DIV.sprites_logo_smaller,DIV.sprites_menu_left,DIV.sprites_menu_right,DIV.sprites_book_left_top,DIV.sprites_book_right_top2,DIV.sprites_book_right_top3,DIV.sprites_book_right_top,DIV.sprites_book_left,DIV.sprites_book_left2,DIV.sprites_book_left_bottom,DIV.sprites_book_left_bottom2,DIV.sprites_book_right_bottom,DIV.sprites_book_right_bottom2,TD.main_cell,A.like,.second_nav {background-image:none!important;background-color:transparent!important}
BODY{background:url("http://i1144.photobucket.com/albums/o492/SamaelDark/web/bk_wrapper.png") repeat 0 0 fixed #1E201F !important}
table[align="right"]{
	background: url("http://i1144.photobucket.com/albums/o492/SamaelDark/web/bk_wrapper.png") repeat 0 0 #CCC !important;
	width: 970px!important;
	position: absolute;
	margin-left: -485px;
	left: 50%
}
.menu{background-color:rgba(181, 243, 255, 0.5)}
DIV.submit_button INPUT{background-color:#FFF}
TEXTAREA,DIV.comment_thumb{box-shadow:4px 4px 12px #444;background-color:rgba(255, 255, 255, 0.75)}
DIV.user_drop_down,DIV.user_drop_down[style="position: absolute; left: -65px; z-index: 100; display: block;"]{background-color:#1f1f1f!important;}
table[style="background-color: #ffff99"]{background-color:rgba(255,255,255,.4)}

/*СКРЫТЫЕ БЛОКИ*/
a.give_present,a.give_reward_fic,a.give_present_small,TD.zakl,TD.footer,DIV.choose_font,DIV.no_flood_warning,table[height="48"],DIV.sprites_book_left_bottom,DIV.sprites_book_right_bottom2,HR,DIV.sprites_book_left,DIV.sprites_book_right_top2,DIV.sprites_book_right_top3,.part_right,td.zakl,td.footer,DIV.user_drop_down[style="position: absolute; left: -65px; z-index: 100; display: block;"],img[src="https://teinon.net/ficbook/design/logo2.png"],.new_year_cat{display:none!important}
#logo_feather{visibility:hidden!important}
}
