css:
@-moz-document domain("linux.org.ru") {
html,body{background-color:#c9c9c9 !important;}
body{font-family: PT Sans Caption !important; font-weight: normal !important;max-width: 100% !important; width: auto !important; margin-top: 1em !important;}
*{font-family: PT Sans Caption !important;line-height: 1.2em !important}
body, textarea, #commentForm, input, code {
font-size: 1rem !important;}
@media screen and (min-width:90em){html{font-size:1em !important}}
body{color:#000000 !important; margin: 25px 10px 10px 10px !important;}
a{color:#0030c0 !important;}
#guser{color:#fff !important;}
#hd .menu .primary a{color:#000000 !important;}
#hd .menu .secondary a{color:#000000 !important;}

.btn, .btn:hover{color: #000000 !important; background-color: #E5E5E5 !important;border-radius: 0 !important;border-color: #000000 !important;}

figure.medium-image {float: none !important;text-align: left;}
#ft-info {display: none !important;}
#bd h2 *{color:#000000 !important;}
#bd .tags a{color:#0030e0 !important;}
#bd .sign a{color:#000000; text-decoration: none !important;}
#bd .sign a:hover{color:#0080C0  !important; text-decoration: underline !important;}
#bd .sign a:visited{color:#606090 !important;}

.msg {clear: bottom !important;}
.sign {clear: bottom !important;}

#bd .messages h1, .messages h2 {margin-top: 5px !important; font-weight: bold !important;color: #000 !important;}
#bd .messages h1, .news h2 {font-size:16pt !important;}

fieldset{border: 1px solid black !important;}
fieldset legend{color: black !important;}
#bd {padding-left: 0em !important; padding-right: 0em !important;}

.infoblock {background-color: #c9c9c9 !important;border: 1px solid !important;moz-border-radius: 0em !important;-webkit-border-radius: 0em !important;-khtml-border-radius: 0em !important;border-radius: 0em !important;}
#bd div.messages div.infoblock {display: none !important;}

.nav li a {border:none !important;background-color: #c9c9c9 !important;}

.fav-buttons {display: none !important;}

#bd h1, h2 {
margin-top: 1em !important;
margin-bottom: .5em !important;
}
.user-remark{color: #ffffff !important}

.grid-row{display: none !important;}

a span.tag{float: right ;border-radius: 0px !important;border: 1px solid black;margin-left: -1px;padding:0px 2px 0px 2px;background-color: inherit !important;}

.quote {display:block !important;border: #000 dotted 1px !important;margin: 0px -1px -1px 0px !important;padding: 0.2em 0px 0px 1em !important;}
div.messages .title{background: none  !important; margin-left: 2px !important;}
.messages .comment .title{background: none  !important;padding: 0px !important;}
.message-table tbody{background-color: #c9c9c9  !important;}
table.nav > tbody > tr > td{background: none  !important; border: 0px solid !important;}
.nav {background: none !important;margin-bottom: 0em !important;margin-top: 0.7em !important;}
.message-table{background: #000000 !important;}
.message-table td{background: #c9c9c9 !important;}
.bluehead{background: none !important;}
.messages .msg:target{background-color: #c9c9c9  !important;}

.msg {moz-border-radius: 0em !important;-webkit-border-radius: 0em !important;-khtml-border-radius: 0em !important;border-radius: 0px !important;}

#bd .forum table thead{background-color:#b0b0b0 !important;}

#bd .forum table th{border-color:#555753 !important;}
#bd .forum table tbody td *{color:#0000ad !important;}
.messages .msg{background:#b0b0b0  !important; border-color: #000000  !important; border-style: solid  !important; border-width: 1px !important;word-wrap: break-word !important; margin-bottom: 5px !important;padding-bottom: 5px !important;}

button, input, select, .valid {border: 1px solid #000 !important;}

.message-w-userpic p span { white-space: normal !important; width: auto !important;}
#bd a:visited{color:#003090 !important;}
#bd .forum table tbody td a:visited{color:#babdb6 !important;}
#navPath a:visited{color:#729FCF !important;}
#bd .forum table tbody tr:hover{background-color:#5c3566  !important;}
#whois_userpic{background:#b0b0b0  !important;border-radius: 0px !important;border: 1px solid !important;}
.code{font-family: Monospace !important;background:#8f8f8f  !important;border: 1px solid !important;-webkit-border-radius: 0em !important;-khtml-border-radius: 0em !important;border-radius: 0em  !important;}
textarea{font-family: PT Sans Caption  !important; font-weight: normal !important;}

#bd .forum table tbody tr:hover {background-color: #d9d9d9 !important;}

#bd .forum table tbody tr td a:visited {color: #858585 !important;}

pre {background-color: #8f8f8f !important;}

pre .shebang, pre .comment, pre .template_comment, pre .javadoc{color: #95FF8F !important;}

#ft{border-top: none !important;padding: 0em !important;}

#bd .column .boxlet{width: 85%  !important; background: none !important; border: 1px solid  !important; padding: 1em !important;}
#bd .column .boxlet h2{background: none !important;}
.boxlet{width: 85%  !important; background: none !important; border: 1px solid  !important; padding: 1em !important; moz-border-radius: 0em !important;-webkit-border-radius: 0em !important;-khtml-border-radius: 0em !important;border-radius: 0em !important;}

.page-number {background-color: #c9c9c9 !important;border: none !important;padding: 2px !important;}

#bd iframe{display: none !important;}

#user-menu{background-color:#c9c9c9 !important;}
#contents{background:#c9c9c9 !important;color: #000 !important;}
.portlet {background: #c9c9c9 !important;border-radius: 0em !important;}
table {background-color: #c9c9c9 !important;border-color: #000 !important;}
#contents-header, #content-article h1, h2, h3, h4, h5{color: #000 !important;}
#commentForm {font-size: 1rem !important;}
input {font-size: 1.0em !important;}
img.photo{width: auto !important;height: auto !important; border-radius: 0 !important;}
code { font-size: 1rem !important}
#bd .messages h1 a, .messages h2 a{color: #000000 !important;}
.news h2 a{margin-top: 5px !important; font-weight: bold !important;}
}
