css:
@-moz-document domain('coinchat.org') {
  #chattext {
    background-color: #161616;
    color: #AAA;
  }
  .message span {
    color: #CCC !important;
  }
  .me {
    color: #FFF;
  }
  ::-webkit-scrollbar-track
  {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    border-radius: 10px;
    background-color: #222;
  }
  ::-webkit-scrollbar
  {
    width: 12px;
    background-color: #222;
  }
  ::-webkit-scrollbar-thumb
  {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
    background-color: #000;
  }
  .chatline .user {
    color: #DDD;
    background-color: #222;
  }
  .chatline:hover, .message:hover {
    background-color: #000;
  }
  .tipbutton {
    background-color: #333;
    color: #AAA;
    text-shadow: none;
    border: 1px solid #666;
    margin-right: 3px;
  }
  .tipbutton:hover {
    background-color: #000;
    color: #FFF;
    text-shadow: none;
    border: 1px solid #999;
  }
  #chat .header {
    color: #AAA;
    background-color: #222;
  }
  #chat {
    background: #161616;
    background-color: #161616;
  }
  body {
    background-image: none;
    background-color: #161616;
  }
  #chatleft {
    color: #AAA;
    background: #161616;
    background-color: #222;
  }
  #chat #chatleft {
    background: #161616;
    background-color: #161616;
    color: #AAA;
  }
  .divider {
    color: #AAA;
    background-color: #000;
    border-bottom: 1px solid #666;
  }
  .roombtn:hover {
    background-color: #000;
  }
  .input {
    background: #161616 !important;
    background-color: #161616;
  }
  #style, #smiley {
    background-color: #333;
    color: #AAA;
    text-shadow: none;
    border: 1px solid #666;
  }
  #smiley:hover, #style:hover {
    background-color: #000;
    color: #AAA;
    text-shadow: none;
    border: 1px solid #999;
  }
  #chatinput {
    background-color: #000;
    color: #DDD;
    border: 1px solid #333;
  }
  #chatsidebar .sideuser {
    background-color: #000;
    color: #AAA;
  }
  .alert {
    background-color: #000;
    border: 1px solid #333;
    text-shadow: none;
  }
  .intro {
    background-color: #222;
    border: 1px solid #333;
  }
  .roombox {
    background-color: #000;
  }
  .suggested {
    background-color: #222;
  }
  input[readonly] {
    cursor: text;
  }
  .label-success {
    background-color: #166616;
  }
  .label-warning, .badge-warning {
    background-color: #d88105;
  }
}
