@-moz-document domain("flickr.com"), domain("flickrhelp.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
	
}

.sub-photo-comments-view .comments-form .comment-form-field,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

body {
	font-family: 'FontAwesome'!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

body 
{
	font-size: 1.5rem;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.ad-card
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.people-card-view .people-card .view-more-footer .view-more-button
{
	padding: 3px 6px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.article,
.container,
.feed-new-page-view .feed-new-page-container .feed-new-page-content-container,
.header,
html.scrolling-layout .fluid-centered
{
	max-width: none!important;
}

.photo-card-content,
.feed-new-page-view .feed-new-page-container .feed-new-page-content-container .feed-column .feed-layout .feed-item
{
	width: auto!important;
}

.feed-new-page-view .feed-new-page-container .feed-new-page-content-container
{
	width: 98%!important;
}

.people-card-view .people-card .people-list-container .people-list .people-layout .person-info-card-view
{
	width: 45%!important;
}

}

@-moz-document domain("flickr.com"), domain("flickrhelp.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.bg-blue:hover span,
.bg-hover-blue:focus, .bg-hover-blue:hover,
.bg-hover-grey:focus, .bg-hover-grey:hover,
.people-card-view .people-card .view-more-footer .view-more-button:hover,
.person-info-card-view .person-info-card .foot .left-container .attribution .username,
.products-pro-view .pro-2018-view-container .header-button:hover,
.sub-photo-view>.sub-photo-container,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.feed-new-page-view .feed-new-page-container .feed-new-page-content-container .feed-column .feed-new-tools-container .filter-menu-button,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.article-content p span:last-child,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.article-content p span:nth-child(2)
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("flickr.com"), domain("flickrhelp.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.feed-batch-view,
.fluid .fluid-droparound-overlay, .fluid .fluid-modal-overlay,
.products-pro-benefits-view .benefit-section
{
	background: #060505!important;
	background-color: #060505!important;
}

.contact-us,
.feed-new-page-view .feed-b,
.photo-well-scrappy-view .height-controller
{
    background-color: #101010!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.default-theme .search-autosuggest-field-view .search-form,
.feed-rail-view .feed-rail .feed-rail-content-container .slim-footer-view,
.people-card-view .people-card .view-more-footer,
.photo-card-view .photo-card .link-footer,
.semi-transparent-theme .search-autosuggest-field-view .search-form,
.sub-photo-comments-view .comments-form .comment-form-field,
.sub-photo-view>.sub-photo-container
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.bg-blue,
.global-nav-view .semi-transparent-theme .gn-signup a,
.products-pro-view .pro-2018-view-container .header-button
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.bg-blue::before,
.global-nav-view .semi-transparent-theme .gn-signup a::before,
.products-pro-view .pro-2018-view-container .header-button::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.bg-blue:hover::before,
.global-nav-view .semi-transparent-theme .gn-signup a:hover::before,
.products-pro-view .pro-2018-view-container .header-button:hover::before
 {
  opacity: 1!important;
}

/* Light Blue 
_____________________________________________________________________________________________________*/

.dark .light .fluid .butt.follow,
.dark .light .fluid button.follow,
.fluid .butt,
.fluid .butt.join.blue,
.fluid .butt.white,
.fluid button,
.fluid button.join.blue,
.fluid button.white,
.light .dark .light .fluid .butt.follow,
.light .dark .light .fluid button.follow,
.people-card-view .people-card .view-more-footer .view-more-button
{	
	background: radial-gradient(circle, #058db7a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.dark .light .fluid .butt.follow::before,
.dark .light .fluid button.follow::before,
.fluid .butt.join.blue::before,
.fluid .butt.white::before,
.fluid .butt::before,
.fluid button.join.blue::before,
.fluid button.white::before,
.fluid button::before,
.light .dark .light .fluid .butt.follow::before,
.light .dark .light .fluid button.follow::before,
.people-card-view .people-card .view-more-footer .view-more-button::before
{
	background: radial-gradient(circle, #000 0%, #058db7 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.dark .light .fluid .butt.follow:hover::before,
.dark .light .fluid button.follow:hover::before,
.fluid .butt.join.blue:hover::before,
.fluid .butt.white:hover::before,
.fluid .butt:hover::before,
.fluid button.join.blue:hover::before,
.fluid button.white:hover::before,
.fluid button:hover::before,
.light .dark .light .fluid .butt.follow:hover::before,
.light .dark .light .fluid button.follow:hover::before,
.people-card-view .people-card .view-more-footer .view-more-button:hover::before
{
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.bg-white,
.feed-layout .photo-card-view .photo-card,
.feed-rail-view .feed-rail .feed-rail-content-container .rail-items-container .rail-item,
.fluid .modal,
.people-card-view .people-card
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.bg-white:hover,
.feed-layout .photo-card-view .photo-card:hover,
.feed-rail-view .feed-rail .feed-rail-content-container .rail-items-container .rail-item:hover,
.fluid .modal:hover,
.people-card-view .people-card:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.basic-banner.foot-banner-style
{
	background: linear-gradient(to bottom, #590505 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.bg-black,
.header,
.global-nav-view .default-theme .global-nav-content
{
	background: linear-gradient(to bottom, #242323 0%, #000 100%)!important;
}

.container-footer,
.footer-full-view .foot,
.sub-photo-view
{
	background: linear-gradient(to top, #242323 0%, #000 80%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.bg-hover-blue:focus, .bg-hover-blue:hover,
.bg-hover-grey:focus, .bg-hover-grey:hover,
.sidenav-item.current-article, .sidenav-item:hover,
.sub-photo-comments-view .comments .comment.droparound-open, .sub-photo-comments-view .comments .comment:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}


}

@-moz-document domain("flickr.com"), domain("flickrhelp.com") {
/* Image Settings */

.card-batch-photo-item,
.people-card-view .people-card .people-list-container .people-list .people-layout .person-info-card-view,
.photo-card-view .photo-card .photo-card-content
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.card-batch-photo-item:hover,
.people-card-view .people-card .people-list-container .people-list .people-layout .person-info-card-view:hover,
.photo-card-view .photo-card .photo-card-content:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("flickr.com"), domain("flickrhelp.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Eyes _______________________________________________________________*/

/* Open ____________________________*/

.flickr-input-append-slot.flex.align-center.justify-center.pa-3 svg {
	background: url(https://i.ibb.co/VSLvZzD/eye-clip-art-symbol-eye-makeup-brown.png) no-repeat!important;
	box-sizing: border-box;
}

/* Close ____________________________*/

Selector {
	background: url(https://i.ibb.co/v3qK5Rf/no-see.png) no-repeat!important;
	box-sizing: border-box;
}

.flickr-input-append-slot.flex.align-center.justify-center.pa-3 svg
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Notification ________________________________________________________________________*/

.notifications-menu-view .gn-title.c-notifications-menu .c-notification-icon {
	background: url(https://i.ibb.co/8Px680k/Notification.png) no-repeat!important;
	box-sizing: border-box;
}

.notifications-menu-view .gn-title.c-notifications-menu .c-notification-icon {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.icon-search,
.search-autosuggest-field-view .search-form .search-icon-button
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-search,
.search-autosuggest-field-view .search-form .search-icon-button
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}


}

@-moz-document domain("flickr.com"), domain("flickrhelp.com") {
/* Social Media */

/* Facebook _____________*/

.ft-facebook
{
	background: url(https://i.ibb.co/bvgwKNN/facebook-48x48.png) no-repeat!important;
	box-sizing: border-box;
}

.ft-facebook
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

/* Instagram _____________*/

.ft-instagram
{
	background: url(https://i.ibb.co/YQ5F02g/Instagram.png) no-repeat!important;
	box-sizing: border-box;
}

.ft-instagram
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
