css:
@-moz-document domain("feedly.com") {
/* Bigger Refresh Button and "Mark as read" for Most Popular Items*/
/* Highlight the No. of Unread Articles in blue.*/
/* I skim the Popular items... Mark them as read... then hit Refresh to get the next 3 Most Popular Items*/
/* You need to hover over the left of the Green bar to show the "Mark as read" link*/

/* Note that to get to Edit and Remove etc..just disable the style for a moment then re enable */

/* Hide title section stuff you don't need day-to-day: Edit & Remove Buttons, Subtitle, Search Box */
span.hAction.tertiary, span.hAction.secondary, #hercule.default,  #searchBoxPlaceholder {display: none !important;}

#feedlyTitleBar {
    margin-left: 5%;
}


img.pageAction {
    height: 25px;
    width: 25px;
    margin-right: 500px;
}

span.action {
    font-size: 125%;
  	color: #000000;
}


img.pageAction.requiresLogin {
    height: 25px;
    width: 25px;
}


#featuredArea h2 {
    width: 100%;
    margin-bottom: 10px;
    background-color: #3f993a;
    font-size: 5%;
    border-style: inset;
    border-color: #000000;
}

span.feedUnreadCountHint {
    font-size: 125%;
    color: #0d05f7;
}


span.hhint {color: #000000;}


/* Hide Ad on Add Content page */
#herculeMobile {display: none !important;}

/* Hide right Sidebar */
#sideArea {display: none !important;}

/* Adjust main section width */
#mainArea, #feedlyPart, #feedlyPage, #timeline {width: 99.21% !important;}

/* Adjust article section width */
.u4Entry {max-width: 100% !important;}

/* Adjust main section width on Home page */
#main_section_Feeds_holder {width: 95.57% !important;}

/* Adjust Full Article view text area width */
.entryBody {max-width: 794px !important;}

/* Adjust Full Article view title area width */
.u100Entry .title {max-width: 793px !important;}

/* Adjust Login page image width */
#bookshelf {max-width: 859px !important;}


/* black mark as read */
span.action {color: #000000;}

/* black mark as read */
span.hhint {color: #000000;}

/* Move reload closer to mark as read */
div.pageActionBar {margin-right: 30%;}

/* Hide Ad on Add Content page */
#herculeMobile {display: none !important;}

/* Hide right Sidebar */
#sideArea {display: none !important;}

/* Adjust main section width */
#mainArea, #feedlyPart, #feedlyPage, #timeline {width: 99.21% !important;}

/* Adjust article section width */
.u4Entry {max-width: 100% !important;}

/* Adjust main section width on Home page */
#main_section_Feeds_holder {width: 95.57% !important;}

/* Adjust Full Article view text area width */
.entryBody {max-width: 794px !important;}

/* Adjust Full Article view title area width */
.u100Entry .title {max-width: 793px !important;}

/* Adjust Login page image width */
#bookshelf {max-width: 859px !important;}
}
