css:
@-moz-document  url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/"){

/* BODY BACKGROUND */

body {
  background:#171c5f url('http://i.imgur.com/VxHgQmP.png') no-repeat bottom left fixed !important;

}

/* Some things may need a solid color to approximate the body background... */

/* "new" text in tag/blog search bar */
.pill.notice {
  color: #111 !important;
}


/* CONTENT BACKGROUND */

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background-color: rgba(255,255,255,0.1)  !important;
}


/* arrow pointing up to home, inbox, etc. */
div.l-header .selection_nipple {
  border-bottom-color: #edf3f5 !important;
}



/* Some things may need a solid color to approximate the content background... */

/* dog-eared corner for post permalink */
#posts .post_full .post_permalink {
  border-right: 9px solid #edf3f5!important;
  border-top: 9px solid #edf3f5 !important;
}

/* "ignore" text in notifications */
.notification .report, .notification .block {
  color: rgba(255,255,255,0.1)  !important;
}


/* ENDLESS SCROLLING LOADING BAR */

#auto_pagination_loader {
  background: #edf3f5 !important;
  text-shadow: none !important; color:#626262!important;

}


/* PAGINATION LINKS */

/* normal color */
#dashboard_controls a, #dashboard_controls .control,
#next_page_link, #previous_page_link,
.blog_menu .selected_blog #open_blog_link {
  color: #edf3f5!important;
}

/* hover color */
#dashboard_controls a:hover,
#next_page_link:hover, #previous_page_link:hover {
  color: #edf3f5 !important;
}

/* background for bottom of page links */
#next_page_link, #previous_page_link {
  background-color: rgba(255,255,255,0.1)  !important;
}

/* FOOTER LINKS */

.footer_links a {
  color: rgba(255,255,255,0.1) !important;
}



/* QUEUE PAGES */

.dashboard_options_form, .tag_psa {
  background-color: #edf3f5;
  border-color: #edf3f5;
  color: rgba(255,255,255,0.1) !important;
}

.dashboard_options_form .dashboard_options_form_disclaimer,
.dashboard_options_form .dashboard_options_form_disclaimer a {
  color: rgba(255,255,255,.5) !important;
}

.post_avatar,
.post_avatar.lighter_blue {
  background-color: #edf3f5 !important;
}

.post_avatar.flat {
  border-color: #edf3f5!important;
}

.publish_on_day {
  color: rgba(255,255,255,.8) !important;
}

.publish_on_time {
  color: rgba(255,255,255,.5) !important;
}



/* OTHER DESIGN TWEAKS */

/* search bar */
div.l-header ::-webkit-input-placeholder {
  color: rgba(255,255,255,.6);
}
div.l-header :-moz-placeholder {
  color: rgba(255,255,255,.6);
}
.search_query {
  color: rgba(255,255,255,.9);
}
.pill.notice {
  background: #edf3f5 !important;
}

/* right column blog controls */
#right_column .controls_section {
  background-color: #edf3f5!important;
}

/* tumblr radar */
#tumblr_radar {
  opacity: 0.4;
  -webkit-transition: opacity 0.3s ease-in-out;
  -moz-transition: opacity 0.3s ease-in-out;
  -o-transition: opacity 0.3s ease-in-out;
  transition: opacity 0.3s ease-in-out;
}
#tumblr_radar:hover { opacity: 1 }

/* notifications */
/*.notification {
  -webkit-transition: background-color 0.1s ease-in-out;
  -moz-transition: background-color 0.1s ease-in-out;
  -o-transition: background-color 0.1s ease-in-out;
  transition: background-color 0.1s ease-in-out;
}*/
.notification:hover {
  background-color: #edf3f5 !important;
}

.new_post {


background: #fff; }


/* (DESIGN NEUTRAL ADJUSTMENTS) */

body:not([id]) {
  background-color: transparent !important;
  background-image: none !important;
}

.notification .avatar_frame, .ui_avatar .frame,
.notification .notification_right .preview_frame {
  border-color: rgba(0,0,0,0.3) !important;
}

#dashboard_settings div.l-content,
#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background-color: #fff !important;
}
*{text-shadow:none !important; color:#626262!important;}

.dashboard_iframe, #right_column {
  background-color: transparent !important;
  background-image: none !important;
}

#dashboard_controls a, #dashboard_controls .control {
  text-shadow: none !important;
}

#right_column:after {
  background: none !important;
}

.dashboard_controls_arrow_first_page,
.dashboard_controls_arrow_left,
.dashboard_controls_arrow_right {
  background-image: none !important;
  height: 20px !important;
  width: 20px !important;
  vertical-align: top !important;
  white-space: nowrap !important;
}
.dashboard_controls_arrow_right {
  text-align: right !important;
}
.dashboard_controls_arrow_first_page:after {
  content: "|←";
}
.dashboard_controls_arrow_left:after {
  content: "←";
}
.dashboard_controls_arrow_right:after {
  content: "→";
}

#posts .post_full .post_permalink {
    background: none !important;
  width: 0 !important;
  height: 0 !important;
  border-bottom: 9px solid #cdcfd1 !important;
  border-left: 9px solid #cdcfd1 !important;
  border-bottom-left-radius: 9px;
  top: 0 !important;
  right: 0 !important;
}

div.l-header .selection_nipple {
  top: 4px !important;
}

}
