css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("eztv.it"), url("http://eztv.it/index.php"), url("http://eztv.it/forum.css")
{







body {background-color: #111 !important;}

#header_logo {display: none !important}

#site_menu {background: #444 !important; border: solid #444 1px !important; -moz-border-radius: 6px !important; color: #999 !important}

.forum_thread_header {background: #222 !important; color: #999 !important;}

.forum_header_border, #divider{border: solid #444 1px !important;}

.section_post_header { border: solid #444 1px !important}

.form_action_text, .form_action_left, .form_action_right {background: #444 !important}


/** LINK PEVIOUSLY SELECTED **/

.epinfo {color: #19D  !important}



.input {background: #444 !important;}

SELECT[name="SearchString"] {background: #999 !important}

.forum_header_border tr, .forum_header_border td {background-color: #222 !important;}

.forum_header_menu {background-color: #999 !important;}


/** LINK **/

a.thread_link {color: #af0 !important;}



td {color: #999 !important;}


/** LINK **/

*|*:link  {color: #FF7401 !important;}



.section_header_column {background-color:#222 !important}

a {color: #999 !important}





.section_post_header {background: #444 !important}

.forum_header_border_normal {border: solid #444 1px !important}

tr {background: #444 !important}

td  {background: #222 !important}





/* -------------------------------------------------------------------------- G r e e n --- */
img[title="Show Description about Game of Thrones"],
img[title="Show Description about Supernatural"],
img[title="Show Description about Grimm"],
img[title="Show Description about The Vampire Diaries"],
img[title="Show Description about Thundercats (2011)"],
img[title="Show Description about True Blood"],
img[title="Show Description about Teen Wolf"],
img[title="Show Description about The Walking Dead"],
img[title="Show Description about Star Wars: The Clone Wars"],
img[title="Show Description about Arrow"],
img[title="Show Description about The Neighbors (2012"],
img[title="Show Description about "],
img[title="Show Description about "],
img[title="Show Description about "],
img[title="Show Description about "]

 {
	background: #af0		!important;
	border: solid 1px #0d0		!important;
	padding: 0px 19px 0px 3px 	!important;
	margin: -1px 0px 0px -3px	!important;
	position: absolute		!important;
}

img[src="http://ezimg.eu/s/1/3/show_info.png"],
img[src="https://ezimg.eu/s/1/3/show_info.png"] {
	opacity: 0.8			!important;
	position: absolute		!important;
}

img[src="http://ezimg.eu/s/2/1/tvrage.png"],
img[src="https://ezimg.eu/s/2/1/tvrage.png"] {
	opacity: 0.6			!important;
	float: right			!important;
}

/** PREVIOSELY SELECTED LINKS **/

a {
	color: #0f7ff0			!important;
}



/** GRID **/

td {
	color: #bbc			!important;
	background: #112		!important;
	border-color: #0a3564		!important;
}



/** HOOVER **/

a:hover {
	color: #0f0			!important;
	text-decoration: none		!important;
}

a.thread_link:hover {
	color: #0af			!important;
}





}
