css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://xianguo.com/reader") {
* {font-family: "Microsoft YaHei" ! important;}  /*雅黑*/

#header,#contextPannel > A {display:none !important;} /*去除一些元素*/

#contextPannel,#channelPanel {position:fixed !important;top:0px!important;}


#spliter {background-color:white !important;width:2px !important;}
#page_title {position:relative !important;top:-3px !important;font-size:1.5em !important;}
#itemList_header {padding:10px 0 0 0 !important;}   /*顶栏微调*/
DIV[style="background-color:#F1F1F1;height:25px;"] {background-color:white !important;}
#ul_0,.feed_ul,#listPannelTop {background-color:#E6E6FA !important;}  /*背景色*/

.feed_ul:hover {background-color:
#ADD8E6 !important;}
.channel_item_selected .feed_ul  {background-color:#B0C4DE !important;} /*鼠标和选择颜色*/


.item_selected {background-color:rgb(239, 237, 238) !important;border-style:dashed !important;border-color:gray !important;
border-width:2px !important;}
.item_selected .title > a {color:#C60 !important;}  /*当前阅读条目*/
 
}
