@-moz-document domain("trello.com") {
/* Saeed Habibi, Twitter: @saeedhbi */
body {
    font-family: tahoma;
    font-size: 8pt;
}

.checklist-item {
    float: right;
    padding: 0 36px 4px 0px;
    direction: rtl;
}

.checklist-item .checklist-item-checkbox , .checklist-item.complete .checklist-item-checkbox {
    right: 5px !important;
    left: initial;
}

.check-item-text p {
    padding-left: 30px !important;
}

.window-main-col .edit textarea.field {
    font-famiLy: tahoma !important;
    font-size: 8pt;
    direction: rtl;
    text-align: right;
}

.new-task.focus .new-task-text {
    font-famiLy: tahoma !important;
    font-size: 8pt;
    direction: rtl;
    text-align: right;
}

.card-detail-desc div, .current-comment p, .js-card-desc {
    text-align: right;
    direction: rtl;
}

.list-card textarea , .new-comment-input, .js-text, .checklist-new-item-text {
    font-famiLy: tahoma !important;
    font-size: 8pt;
  	direction: rtl;
}

.board-list a {
    font-size: 12px
}
}
