css:
@-moz-document url("http://www.baidu.com/"), url-prefix("http://www.baidu.com/home"), url-prefix("http://www.baidu.com/index.php"),url("http://www.baidu.com/###") {
html:not([xmlns]) #lg,.s_btn_wr{
  background:url(http://imgsrc.baidu.com/forum/pic/item/7da86b899e510fb3f9270773d833c895d0430cf9.jpg) no-repeat!important}
html:not([xmlns])>body{
  overflow:hidden;
  height:100%!important;
  min-width:1000px;
  min-height:562px!important;
  background-color:rgb(102,102,102)!important;

  background-image:url(/*[[backgroundImage]]*/)!important;
  background-repeat:no-repeat!important;
  background-position:top center!important;
  background-attachment:fixed!important;
  background-size:100% 100%!important}
#wrapper{
  height:100%}
html:not([xmlns]),div#u,#ftCon{
  border-width:0px!important;
  background-color:rgb(51,51,51)!important;
  font-size:13px}
html:not([xmlns]) a:link{
  text-decoration:none!important}
html:not([xmlns]) #m{
  position:relative;
  top:-35px;
  width:100%!important;
  height:100%;
  max-width:2560px;
  min-width:800px;
  min-height:562px;
  margin:auto}
html:not([xmlns]) #lg{
  position:absolute;
  top:30%;
  left:8%;
  width:175px;
  height:35px;
  z-index:6}
#fm{
  position:absolute!important;
  top:30%;
  left:8%;
  z-index:10;
  width:421px;
  padding:0!important;
  margin:0 0 0 155px}
#fm>form>div{
  border-left:none!important;
  border-right:none!important;
  width:421px!important}
.s_ipt_wr{
  border:none!important;
  height:37px!important;
  width:421px!important;  
  background:none!important;
  background-color:rgb(255,255,255)!important}
#kw.s_ipt{
  -moz-appearance:none;
  width:375px!important;
  height:28px!important;
  border:none;
  background:none}
.s_btn_wr{
  position:relative;
  top:-32px;
  left:-5px;
  width:28px!important;
  float:right;
  background-position:-149px -37px!important}
.s_btn_wr>input{
  width:28px!important;
  height:28px;
  opacity:0}
#lm{
  visibility:hidden}
div#u{
  margin:auto;
  position:relative;
  min-height:26px;
  min-width:800px;
  max-width:2560px;
  padding:9px 10px 0 0!important;
  z-index:9;
  color:transparent!important}
html:not([xmlns]) #nv{
   margin:auto;
   position:relative;
   top:-1px;
   padding:8px 0 0 0!important;
   text-indent:0px!important;
   z-index:9;
   margin-right:350px!important;
   margin-left:10px!important}
#lk{
  position:absolute;
  top:-23px;
  left:392px;
  z-index:10;
  color:rgb(102,102,102);
  font-size:13px;
  font-weight:700}

/*[[chromeText]]*/
div#u>*:not([name='tj_setting']):not(#lb),div#u>span>a,html:not([xmlns]) #nv>a,#lk a{
   color:rgb(153,153,153)!important}
html:not([xmlns]) #nv>*,#lk>*{
   font-size:13px!important}
html:not([xmlns]) #nv>a,#lk a{
   font-weight:700}
html:not([xmlns]) #nv>b{
   color:rgb(255,166,21)!important}
#userMenu,#userCenterMenu{
   border:none!important;
   font-size:13px}
.userMenu a{
  color:rgb(153,153,153)!important;
  background-color:rgb(255,255,255)}
.userMenu a:hover{
  background-color:rgb(235,235,235)!important}
.userMenu ul{
  border:none!important}
#ftCon{
  margin:auto;
  position:absolute;
  bottom:0!important;
  height:27px!important;
  min-width:800px;
  max-width:2560px;
  padding-top:8px;
  text-align:right!important}
#ftConw{
  margin-right:0!important}
#ftConw>*,#ftConw a{
  color:rgb(102,102,102)!important}
#lh{
  padding:0 20px 0 10px!important;
  float:right;
  position:relative;
  bottom:14px}
#cp{
  position:relative;
  top:2px;
  margin-right:340px!important}
html:not([xmlns]) #lg>*,#ftConw>*:first-child,#cp>img{
  display:none}
html:not([xmlns]) #nv>a:hover,div#u>*:not([name='tj_setting']):not(#lb):hover,#ftCon a:not([name='tj_duty']):hover,#lk a:hover{
  text-decoration:underline!important}
html:not([xmlns]) div[id^='sd_']{
  border-top:solid 1px rgb(204,204,204)!important;
  border-bottom:solid 1px rgb(204,204,204)!important}
#user>a,#user_center>a{
  background:none!important;
  background:url(http://su.bdimg.com/static/skin/img/skin_2bac2a1f.png) no-repeat right -110px!important}
#user i,#user_center i{
  background:none!important}  
#user>a{
  font-weight:normal!important}
.nl>a{
  border-top-color:rgb(235,235,235)!important}
a[name='tj_setting']{
  background:url(http://cn.bing.com/s/a/hpc8.png) no-repeat}
a[name='tj_setting']{
  position:absolute;
  height:20px;
  width:20px;
  top:7px;
  right:13px;
  background-position:-121px -71px;
  color:transparent!important;
  overflow:hidden}
a[name='tj_setting']:hover{
  background-position:-121px -49px}
a[name='tj_supper']{
  margin-right:42px!important}
.reg{
  margin-right:36px!important}
a[name='tj_duty']:before,a[name='tj_duty']:after,#cp:after{
  content:' | '}
#lb:link{
  color:rgb(153,153,153)!important}
#lb:hover{
  color:rgb(255,255,255)!important;
  font-weight:700;}
#lb:active{
  color:rgb(255,166,21)!important}
#passport-login-pop div,#passport-login-pop span,#passport-login-pop li{
  text-decoration:none!important}
#TANGRAM__PSP_4__title,#TANGRAM__PSP_4__body,#TANGRAM__PSP_4__content{
  background-color:rgb(255,255,255)!important;
  border-color:transparent!important}
#TANGRAM__PSP_4__titleText{
  color:rgb(102,102,102)!important}
.tab{
  border:none!important}
li[class~='tab-item']{  
  position:absolute!important;
  transition:left 500ms ease-in-out}
li[class~='tab-item']>a{
  border:solid 1px rgb(212,212,212)!important}
.tab-item{
  left:222px;
  z-index:1}
.tab-item.tab-selected{
  left:116px;
  z-index:2}
#TANGRAM__PSP_10__operateTipsWrapper{
  border-color:rgb(153,218,214)!important;
  background-color:rgb(199,249,245)!important}
.pass-operate-down-a{
  color:rgb(153,218,214)!important}
.pass-operate-down-b{
  color:rgb(199,249,245)!important}
#TANGRAM__PSP_10__unameLoginWrapper{
  position:absolute;
  top:112px;
  left:140px;
  z-index:1}
#TANGRAM__PSP_10__suggestionWrapper{
  z-index:2}
#TANGRAM__PSP_10__verifyCodeImgWrapper{
  margin-top:33px!important}    
#pass-phoenix{
  background:none!important}
#TANGRAM__PSP_10__unameLoginLink,.pass-fgtpwd,#pass-user-login{
  color:rgb(38,114,236)!important}
.pass-login-pop-content>:last-child>a,.pass-sms-btn,.bd-acc-list>*,#TANGRAM__PSP_10__verifyCodeChange{
  color:rgb(0,51,204)!important}
.pass-login-pop-content>:last-child{      
  background:none!important} 
.pass-sms-btn{
  background-position:-384px 2px!important;
  border:solid 1px transparent!important}
.bd-acc-list>*{
  border:solid 1px transparent!important}
.bd-acc-list>:first-child{
  background-position:0 -38px!important}
.bd-acc-list>:nth-child(2){
  background-position:-320px -78px!important}
.pass-sms-btn:hover,.bd-acc-list>*:hover{
  border-color:rgba(235,235,235,.5) rgba(210,210,210,.9) rgba(210,210,210,.9) rgba(235,235,235,.5)!important;
  background-color:rgba(35,35,35,.7)!important;
  color:rgb(220,220,220)!important}          
#TANGRAM__PSP_10__error>*>*{
  color:rgb(38,114,236)!important}
}
