css:
@-moz-document domain("hummingbird.me") {

body {
  background-color:#44484F !important;
}
blockquote p {
  color:#fff !important;
}
blockquote {
  color:#fff !important;
}
ul {
  color:#d6d6d6 !important;
}
strong, b {
  color:#d6d6d6 !important;
}
ol {
  color:#d6d6d6 !important;
}
.redactor_editor, .redactor_editor:focus {
  background:#626b73 !important;
}
.redactor_box {
  border:1px solid #56646C !important;
}   
.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
  background-color:#44484F !important;
}
.nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-tabs>.active>a:focus {
  background-color:#44484F !important;
}
p {
  color:#2C3D47 !important;
}
div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, p, blockquote, th, td {
  color:#C1C1C1 !important;
}
.community .forum-container .reply-header h3 span{
  background-color:#44484F !important;
}
.community .forum-container .discussions {
  background-color:#626b73 !important;
}
.community .forum-container .topic-view .topic-info p {
  color:#d6d6d6 !important;
}
.community .forum-container header {
  background-color:#626b73 !important;
}
.anime-show .actor .character {
  background-color:#626b73 !important;
}
.anime-show .actor .voice-actor {
  background-color:#626b73 !important;
}
.anime-explore .single-wrapper .popular-card {
  background-color:#626b73 !important;
}
.anime-explore .single-wrapper .popular-card {
  box-shadow:#44484F 0px 2px 0px !important;
}
.anime-explore .single-wrapper .popular-card {
  -webkit-box-shadow:#44484F 0px 2px 0px !important;
}
.anime-explore .forum-block {
  background-color:#626b73 !important;
}
.panel {
  background-color:#626b73 !important;
}
.community a {
  color:#2C3D47 !important;
}
.option-bar {
  background-color:#626b73 !important;
}
.option-bar {
  border-top:1px solid #56646C !important;
}
.option-bar {
  border-bottom:1px solid #56646C !important;
}
.option-bar .options ul li.active {
  background-color:#626b73 !important;
}
.option-bar .options ul li a {
  color:#2C3D47 !important;
}
.option-bar .options ul li {
  border-left:1px solid #56646C !important;
}
.feed .feed-list li.feed-item {  
  border-bottom:1px solid #56646C !important;
}    
.feed .feed-list li.feed-item {
  background-color:#B0B5B9 !important;
}
.community .forum-container .topic-view .topic-info .sub-text {
  border-top:1px solid #56646C !important;
}
.community .forum-container .discussions .toolbar {
  border-top:1px solid #56646C !important;
}
a {
  color:#CA6B3C !important;
}
.feed .feed-list li.feed-item .feed-wrapper .quote-preview {
  color:#E5E5E5 !important;
}
.feed .feed-list li.feed-item .feed-wrapper .feed-divider {
  border-color:#9AA2A7 !important;
}
.home-content .forum-block ul .topic-row {
  border-bottom:1px solid #9AA2A7 !important;
}  
.home-content .forum-block {
    background-color:#B0B5B9 !important;
}
.home-content .trending-block {
    background-color:#B0B5B9 !important;
}
.home-content .trending-block ul .topic-row {
    border-bottom:1px solid #56646C !important;
}
.home-content .forum-block hr {
    border:1px solid #56646C !important;
}
.home-content .trending-block hr {
    border:1px solid #56646C !important;
}
.feed .feed-list li.feed-item .feed-wrapper .time .time-ago {
